import React, { useState } from 'react';

function Header() {
  const [showRegister, setShowRegister] = useState(false); // Состояние для отображения модального окна регистрации
  const [showLogin, setShowLogin] = useState(false); // Состояние для отображения модального окна авторизации
  const [username, setUsername] = useState(''); // Состояние для хранения введенного имени пользователя
  const [password, setPassword] = useState(''); // Состояние для хранения пароля

  const handleRegisterSubmit = async (event) => {
    event.preventDefault();
    const userData = { username, password };

    try {
      const response = await fetch('http://localhost:8000/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        alert('User registered successfully');
        setShowRegister(false); // Закрыть окно регистрации
      } else {
        alert('Error during registration');
      }
    } catch (error) {
      console.error(error);
      alert('Error during registration');
    }
  };

  const handleLoginSubmit = async (event) => {
    event.preventDefault();
    const userData = { username, password };

    try {
      const response = await fetch('http://localhost:8000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        alert('Login successful');
        setShowLogin(false); // Закрыть окно авторизации
      } else {
        alert('Invalid credentials');
      }
    } catch (error) {
      console.error(error);
      alert('Error during login');
    }
  };

  return (
    <header className="header">
      <div className="right-side">
        <button className="register-button" onClick={() => setShowRegister(true)}>
          Зарегистрироваться
        </button>
        <button className="login-button" onClick={() => setShowLogin(true)}>
          Войти
        </button>
      </div>

      {/* Модальное окно для регистрации */}
      {showRegister && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowRegister(false)}>
              &times;
            </span>
            <h2>Регистрация</h2>
            <form onSubmit={handleRegisterSubmit}>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button type="submit">Зарегистрироваться</button>
            </form>
          </div>
        </div>
      )}

      {/* Модальное окно для авторизации */}
      {showLogin && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowLogin(false)}>
              &times;
            </span>
            <h2>Войти</h2>
            <form onSubmit={handleLoginSubmit}>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button type="submit">Войти</button>
            </form>
          </div>
        </div>
      )}
    </header>
  );
}

export default Header;
