import axios from 'axios';
import React, { useEffect, useState } from 'react';

window.appGlobals = {
  selectedCameraId: null,
  activityLogId: null
};

const EventsPage = () => {
  const [eventsData, setEventsData] = useState([]);
  const [pets, setPets] = useState([]);
  const [resultsData, setResultsData] = useState([]);
  const [restActivityData, setRestActivityData] = useState([]);
  const [showTrackingOptions, setShowTrackingOptions] = useState(false);
  const [showResultsOptions, setShowResultsOptions] = useState(false);
  const [trackingTime, setTrackingTime] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [cameras, setCameras] = useState([]);
  const [showCameraModal, setShowCameraModal] = useState(false);
  const [selectedCameraId, setSelectedCameraId] = useState(null);
  const [pendingDuration, setPendingDuration] = useState(null);
  const [isMonitoringActive, setIsMonitoringActive] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [resultsPeriod, setResultsPeriod] = useState('1 день'); // Изменено на '1 день' по умолчанию
  const [selectedPetId, setSelectedPetId] = useState(null); // Добавлено для хранения выбранного питомца

  // Функция для получения URL изображения
  const getImageUrl = (pet) => {
    if (!pet?.image) return null;

    try {
      let imagePath = pet.image;
      if (!imagePath.startsWith('/uploads') && !imagePath.startsWith('http')) {
        imagePath = `/uploads/${imagePath}`;
      }
      const decodedPath = decodeURIComponent(imagePath);
      if (!decodedPath.startsWith('http')) {
        return `http://localhost:8000${decodedPath}?t=${Date.now()}`;
      }
      return `${decodedPath}?t=${Date.now()}`;
    } catch (e) {
      console.error('Ошибка обработки URL:', e);
      return null;
    }
  };

  // Загрузка данных при монтировании компонента
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [petsRes, restRes, resultsRes, eventsRes, camerasRes] = await Promise.all([
          axios.get('http://localhost:8000/pets'),
          axios.get('http://localhost:8000/results_rest_and_activity'),
          axios.get('http://localhost:8000/results_eat_and_toilet'),
          axios.get('http://localhost:8000/events'),
          axios.get('http://localhost:8000/cameras')
        ]);

        setPets(petsRes.data?.data || petsRes.data || []);
        setRestActivityData(restRes.data || []);
        setResultsData(resultsRes.data || []);
        setCameras(camerasRes.data?.data || []);

        // Группировка событий
        const events = eventsRes.data || [];
        const groupedData = events.reduce((acc, event) => {
          const { pets_id, events_id } = event;
          if (!acc[pets_id]) {
            acc[pets_id] = {
              pets_id,
              events: { 1: 0, 2: 0, 3: 0, 4: 0 }
            };
          }
          acc[pets_id].events[events_id] += 1;
          return acc;
        }, {});
        setEventsData(Object.values(groupedData));

      } catch (error) {
        console.error('Ошибка при загрузке данных:', error);
      }
    };

    fetchData();

    // Проверка активного отслеживания
    const savedTracking = localStorage.getItem('petTracking');
    if (savedTracking) {
      const { endTime: savedEndTime, duration } = JSON.parse(savedTracking);
      const endTimeDate = new Date(savedEndTime);
      const now = new Date();

      if (endTimeDate > now) {
        const secondsLeft = Math.floor((endTimeDate - now) / 1000);
        setEndTime(endTimeDate);
        setTimeLeft(secondsLeft);
        setTrackingTime(duration);
        setIsMonitoringActive(true);
      } else {
        localStorage.removeItem('petTracking');
      }
    }
  }, []);

  // Таймер отслеживания
  useEffect(() => {
    if (!endTime) return;

    const timer = setInterval(async () => {
      const now = new Date();
      const secondsLeft = Math.floor((endTime - now) / 1000);

      if (secondsLeft <= 0) {
        clearInterval(timer);
        setTimeLeft(null);
        setTrackingTime(null);
        setEndTime(null);
        setIsMonitoringActive(false);
        localStorage.removeItem('petTracking');
        try {
          await stopMonitoring();
        } catch (error) {
          console.error('Ошибка при автоматической остановке мониторинга:', error);
        }
      } else {
        setTimeLeft(secondsLeft);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [endTime]);

  // Обновление глобальной переменной камеры
  useEffect(() => {
    if (selectedCameraId !== null) {
      window.appGlobals.selectedCameraId = selectedCameraId;
    }
  }, [selectedCameraId]);

  // Функции для работы с мониторингом
  const logActivity = async () => {
    try {
      const response = await axios.post('http://localhost:8000/activity_log');
      if (response.data?.log_id) {
        window.appGlobals.activityLogId = response.data.log_id;
      }
      return response.data?.log_id;
    } catch (error) {
      console.error('Ошибка при записи в лог:', error);
      throw error;
    }
  };

  const stopMonitoring = async () => {
    try {
      const response = await axios.post('http://localhost:8000/stop_rtsp_monitoring');
      console.log('Мониторинг остановлен:', response.data);
      setTimeLeft(null);
      setTrackingTime(null);
      setEndTime(null);
      setIsMonitoringActive(false);
      localStorage.removeItem('petTracking');
      return response.data;
    } catch (error) {
      console.error('Ошибка при остановке мониторинга:', error);
      throw error;
    }
  };

  const startTracking = async (duration, seconds) => {
    const endTime = new Date();
    endTime.setSeconds(endTime.getSeconds() + seconds);

    setEndTime(endTime);
    setTimeLeft(seconds);
    setTrackingTime(duration);
    setShowTrackingOptions(false);
    setIsMonitoringActive(true);

    localStorage.setItem('petTracking', JSON.stringify({
      endTime: endTime.toISOString(),
      duration
    }));

    try {
      await logActivity();

      const formData = new FormData();
      formData.append('camera_id', window.appGlobals.selectedCameraId);
      formData.append('activity_id', window.appGlobals.activityLogId);

      const response = await axios.post(
        'http://localhost:8000/start_rtsp_monitoring',
        formData
      );

      console.log('Мониторинг запущен:', response.data);

    } catch (error) {
      console.error('Ошибка при запуске мониторинга:', error);
    }
  };

  const handleStartTracking = (duration) => {
    setPendingDuration(duration);
    setShowCameraModal(true);
    setShowTrackingOptions(false);
  };

  const confirmCameraSelection = () => {
    if (!selectedCameraId) {
      alert('Пожалуйста, выберите камеру');
      return;
    }

    window.appGlobals.selectedCameraId = selectedCameraId;

    switch(pendingDuration) {
      case '2 часа': 
        startTracking(pendingDuration, 2 * 60 * 60);
        break;
      case '10 часов':
        startTracking(pendingDuration, 10 * 60 * 60);
        break;
      case '1 день':
        startTracking(pendingDuration, 24 * 60 * 60);
        break;
      default:
        return;
    }

    setShowCameraModal(false);
    setPendingDuration(null);
  };

  // Обработчик просмотра результатов
  const handleViewResults = async (period, petId) => {
    console.log(`Просмотр результатов за ${period} для питомца ${petId}`);
    setResultsPeriod(period);
    setSelectedPetId(petId);
    setShowResultsOptions(false);

    try {
      const [resultsResponse, activityResponse] = await Promise.all([
        axios.get('http://localhost:8000/results_eat_and_toilet'),
        axios.get('http://localhost:8000/results_rest_and_activity')
      ]);

      setResultsData(resultsResponse.data || []);
      setRestActivityData(activityResponse.data || []);
      setShowResults(true);
    } catch (error) {
      console.error('Ошибка при загрузке данных:', error);
      setShowResults(false);
    }
  };

  // Вспомогательные функции
  const toggleTrackingOptions = () => {
    setShowTrackingOptions(!showTrackingOptions);
    setShowResultsOptions(false);
  };

  const toggleResultsOptions = () => {
    setShowResultsOptions(!showResultsOptions);
    setShowTrackingOptions(false);
  };

  const formatTime = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${hours.toString().padStart(2, '0')} ч ${minutes.toString().padStart(2, '0')} мин ${secs.toString().padStart(2, '0')} сек`;
  };

  // Рендеринг таблицы результатов
  const renderResultsTable = (petId) => {
    const formatMinutes = (minutes) => {
      if (minutes === null || minutes === undefined) return 'Н/Д';
      const mins = Math.round(minutes);
      if (mins < 60) return `${mins} мин`;
      const hours = Math.floor(mins / 60);
      const remainingMinutes = mins % 60;
      return `${hours} ч ${remainingMinutes} мин`;
    };

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const isSameDay = (date1, date2) => {
      if (!date1 || !date2) return false;
      return (
        date1.getFullYear() === date2.getFullYear() &&
        date1.getMonth() === date2.getMonth() &&
        date1.getDate() === date2.getDate()
      );
    };

    const pet = pets.find(p => p.id === petId) || {};
    const petAge = pet.year ? new Date().getFullYear() - parseInt(pet.year) : null;
    const isCat = ['кот', 'кошка'].includes(pet.animal_type?.toLowerCase());
    const isDog = ['собака', 'пес'].includes(pet.animal_type?.toLowerCase());

    // Улучшенная функция для получения данных за день
    const getDayData = (date) => {
      const dateStr = date.toISOString().split('T')[0];
      const dateStrVarchar = date.toLocaleDateString('ru-RU').split('.').reverse().join('-'); // Формат для varchar(10)

      // Для базы results_eat_and_toilet (date - varchar(10))
      const eatData = resultsData.filter(item => 
        item.pets_id == petId && 
        item.events_id == 1 && 
        (item.date === dateStr || item.date === dateStrVarchar)
      );

      const toiletData = resultsData.filter(item => 
        item.pets_id == petId && 
        item.events_id == 4 && 
        (item.date === dateStr || item.date === dateStrVarchar)
      );

      // Для базы results_reast_and_activity (date_monitoring - date)
      const activityData = restActivityData.filter(item => {
        const itemDate = item.date_monitoring 
          ? new Date(item.date_monitoring).toISOString().split('T')[0] 
          : null;
        return item.pets_id == petId && 
               item.events_id == 2 && 
               itemDate === dateStr;
      });

      const restData = restActivityData.filter(item => {
        const itemDate = item.date_monitoring 
          ? new Date(item.date_monitoring).toISOString().split('T')[0] 
          : null;
        return item.pets_id == petId && 
               item.events_id == 3 && 
               itemDate === dateStr;
      });

      return {
        eatCount: eatData.reduce((sum, item) => sum + (item.count || 0), 0),
        toiletCount: toiletData.reduce((sum, item) => sum + (item.count || 0), 0),
        activityMinutes: activityData.reduce((sum, item) => sum + (item.minutes || 0), 0),
        restMinutes: restData.reduce((sum, item) => sum + (item.minutes || 0), 0),
        date: date,
        hasData: eatData.length > 0 || toiletData.length > 0 || activityData.length > 0 || restData.length > 0
      };
    };

    const getTodayData = () => getDayData(today);

    const getWeekData = () => {
      const weekData = [];
      for (let i = 0; i < 7; i++) {
        const date = new Date(today);
        date.setDate(date.getDate() - i);
        weekData.push(getDayData(date));
      }
      return weekData;
    };

    const getLatestData = () => {
      // Находим последний activity_id для выбранного питомца
      const petActivities = [
        ...resultsData.filter(item => item.pets_id == petId),
        ...restActivityData.filter(item => item.pets_id == petId)
      ];

      if (petActivities.length === 0) {
        return { noData: true, message: 'Нет данных об активностях' };
      }

      const lastActivityId = Math.max(...petActivities.map(item => item.activity_id));

      const lastEatToilet = resultsData.filter(item => 
        item.pets_id == petId && 
        item.activity_id === lastActivityId
      );

      const lastRestActivity = restActivityData.filter(item => 
        item.pets_id == petId && 
        item.activity_id === lastActivityId
      );

      return {
        eatCount: lastEatToilet.find(item => item.events_id == 1)?.count || 0,
        toiletCount: lastEatToilet.find(item => item.events_id == 4)?.count || 0,
        activityMinutes: lastRestActivity.find(item => item.events_id == 2)?.minutes || 0,
        restMinutes: lastRestActivity.find(item => item.events_id == 3)?.minutes || 0,
        noData: false
      };
    };

    const getDailyNorms = () => {
      if (!petAge || (!isCat && !isDog)) return {
        eating: 'Н/Д',
        toilet: 'Н/Д',
        activity: 'Н/Д',
        rest: 'Н/Д'
      };

      if (petAge <= 1) {
        return isCat ? {
          eating: '3-4 раза',
          toilet: '6-8 раз',
          activity: '3-5 часов',
          rest: '19-21 час'
        } : {
          eating: '3-4 раза',
          toilet: '8-10 раз',
          activity: '4-6 часов',
          rest: '18-20 часов'
        };
      }
      else if (petAge > 1 && petAge < 7) {
        return isCat ? {
          eating: '2-3 раза',
          toilet: '2-4 раза',
          activity: '10 часов',
          rest: '14 часов'
        } : {
          eating: '2 раза',
          toilet: '3-5 раз',
          activity: '12 часов',
          rest: '12 часов'
        };
      }
      else {
        return isCat ? {
          eating: '2-3 раза',
          toilet: '3-5 раз',
          activity: '4 часа',
          rest: '20 часов'
        } : {
          eating: '2 раза',
          toilet: '4-6 раз',
          activity: '8 часов',
          rest: '16 часов'
        };
      }
    };

    const dailyNorms = getDailyNorms();

    const renderDayTable = (dayData, showDate = true) => {
      return (
        <div className="day-table-container">
          {showDate && (
            <h4 className="day-header">
              {dayData.date.toLocaleDateString('ru-RU', {
                weekday: 'long',
                day: 'numeric',
                month: 'long'
              })}
              {isSameDay(dayData.date, today) && ' (сегодня)'}
            </h4>
          )}

          <table className="event-table">
            <thead>
              <tr>
                <th>Показатели</th>
                <th>Собранные данные</th>
                <th>Средняя норма в сутки</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Количество кормления</td>
                <td>{dayData.eatCount || '0'}</td>
                <td>{dailyNorms.eating}</td>
              </tr>
              <tr>
                <td>Количество походов в туалет</td>
                <td>{dayData.toiletCount || '0'}</td>
                <td>{dailyNorms.toilet}</td>
              </tr>
              <tr>
                <td>Время активности</td>
                <td>{formatMinutes(dayData.activityMinutes)}</td>
                <td>{dailyNorms.activity}</td>
              </tr>
              <tr>
                <td>Время сна</td>
                <td>{formatMinutes(dayData.restMinutes)}</td>
                <td>{dailyNorms.rest}</td>
              </tr>
            </tbody>
          </table>
        </div>
      );
    };

    // Рендеринг в зависимости от периода
    if (resultsPeriod === '1 неделя') {
      const weekData = getWeekData();

      return (
        <div className="week-results-container">
          {weekData.map((dayData, index) => (
            <div key={index} className="day-results">
              {dayData.hasData ? (
                renderDayTable(dayData)
              ) : (
                <div className="no-data-message">
                  <h4 className="day-header">
                    {dayData.date.toLocaleDateString('ru-RU', {
                      weekday: 'long',
                      day: 'numeric',
                      month: 'long'
                    })}
                    {index === 0 && ' (сегодня)'}
                  </h4>
                  <p>В этот день отслеживание не проводилось</p>
                </div>
              )}
            </div>
          ))}
        </div>
      );
    }

    const displayData = resultsPeriod === '1 день' ? getTodayData() : getLatestData();

    if (displayData.noData) {
      return (
        <div className="pet-results-container">
          <div className="no-data-message">
            <p>{displayData.message}</p>
            {resultsPeriod === '1 день' && (
              <p className="current-date">
                {today.toLocaleDateString('ru-RU', {
                  day: 'numeric',
                  month: 'long',
                  year: 'numeric'
                })}
              </p>
            )}
          </div>
        </div>
      );
    }

    return (
      <div className="pet-results-container">
        {resultsPeriod === '1 день' && (
          <p className="date-info">
            Данные за {today.toLocaleDateString('ru-RU', {
              day: 'numeric',
              month: 'long',
              year: 'numeric'
            })}
          </p>
        )}
        {resultsPeriod === 'последние результаты' && (
          <p className="date-info">Последние результаты мониторинга</p>
        )}

        {renderDayTable(displayData, false)}
      </div>
    );
  };

  return (
    <div className="events-page">
      <h2>Мониторинг активности питомцев</h2>

      {/* Кнопки управления */}
      <div className="controls">
        <button onClick={toggleTrackingOptions} className="control-button">
          {isMonitoringActive ? `Остановить (${formatTime(timeLeft)})` : 'Начать отслеживание'}
        </button>

        {showTrackingOptions && (
          <div className="options-dropdown">
            <button onClick={() => handleStartTracking('2 часа')}>2 часа</button>
            <button onClick={() => handleStartTracking('10 часов')}>10 часов</button>
            <button onClick={() => handleStartTracking('1 день')}>1 день</button>
          </div>
        )}

        <button onClick={toggleResultsOptions} className="control-button">
          Показать результаты
        </button>

        {showResultsOptions && (
          <div className="options-dropdown">
            <button onClick={() => setResultsPeriod('1 день')}>1 день</button>
            <button onClick={() => setResultsPeriod('1 неделя')}>1 неделя</button>
            <button onClick={() => setResultsPeriod('последние результаты')}>Последние результаты</button>
          </div>
        )}
      </div>

      {/* Модальное окно выбора камеры */}
      {showCameraModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Выберите камеру</h3>
            <select 
              value={selectedCameraId || ''}
              onChange={(e) => setSelectedCameraId(Number(e.target.value))}
            >
              <option value="">-- Выберите камеру --</option>
              {cameras.map(camera => (
                <option key={camera.id} value={camera.id}>
                  {camera.name} (ID: {camera.id})
                </option>
              ))}
            </select>
            <div className="modal-buttons">
              <button onClick={confirmCameraSelection}>Подтвердить</button>
              <button onClick={() => setShowCameraModal(false)}>Отмена</button>
            </div>
          </div>
        </div>
      )}

      {/* Список питомцев */}
      <div className="pets-grid">
        {pets.map(pet => (
          <div key={pet.id} className="pet-card">
            <div className="pet-image-container">
              <img 
                src={getImageUrl(pet)} 
                alt={pet.name} 
                className="pet-image"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/150';
                }}
              />
            </div>
            <div className="pet-info">
              <h3>{pet.name}</h3>
              <p>Тип: {pet.animal_type}</p>
              <p>Возраст: {pet.year ? new Date().getFullYear() - pet.year : 'Н/Д'}</p>

              <div className="pet-actions">
                <button 
                  onClick={() => handleViewResults('1 день', pet.id)}
                  className="results-button"
                >
                  Результаты за день
                </button>
                <button 
                  onClick={() => handleViewResults('1 неделя', pet.id)}
                  className="results-button"
                >
                  Результаты за неделю
                </button>
                <button 
                  onClick={() => handleViewResults('последние результаты', pet.id)}
                  className="results-button"
                >
                  Последние результаты
                </button>
              </div>
            </div>

            {/* Отображение результатов для выбранного питомца */}
            {showResults && selectedPetId === pet.id && (
              <div className="pet-results">
                {renderResultsTable(pet.id)}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
