import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';

function Pet() {
  const [chartData, setChartData] = useState(null);

  // useEffect для загрузки файла Kevin.xlsx при рендере
  useEffect(() => {
    const loadExcelFile = async () => {
      try {
        const response = await fetch('/anakot/public/Kevin.xlsx');
        const data = await response.arrayBuffer();
        const workbook = XLSX.read(data, { type: 'array' });
        const sheetName = workbook.SheetNames[0];
        const sheet = workbook.Sheets[sheetName];
        const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });

        // Преобразование данных для графика
        const labels = jsonData.slice(1).map(row => row[0]); // Время
        const values = jsonData.slice(1).map(row => row[1]); // Активность

        setChartData({
          labels,
          datasets: [
            {
              label: 'Активность Кевина',
              data: values,
              borderColor: 'rgba(75,192,192,1)',
              backgroundColor: 'rgba(75,192,192,0.2)',
              tension: 0.4,
            },
          ],
        });
      } catch (error) {
        console.error('Ошибка при загрузке файла:', error);
      }
    };

    loadExcelFile();
  }, []); // Пустой массив зависимостей означает, что useEffect выполнится только один раз при монтировании компонента

  return (
    <div className="pet-info">
      <div className="pet-photo">
        <img src="/Kevin.jpg" alt="Фото питомца" />
      </div>
      <div className="pet-details">
        <h2>Имя: Кевин</h2>
        <p><strong>Возраст:</strong> 3 года</p>
        <p><strong>Порода:</strong> Кот</p>
        <p><strong>Особенности:</strong> Очень любопытный и игривый. Любит пакеты.</p>
      </div>

      <section className="daily-activity">
        <h2>Дневник активности Кевина</h2>
        {chartData ? (
          <Line
            data={chartData}
            options={{
              responsive: true,
              plugins: {
                legend: { display: true },
              },
            }}
          />
        ) : (
          <p>Загрузка данных...</p>
        )}
      </section>
    </div>
  );
}

export default Pet;
