import set from "lodash/set"
import { parseSearch } from "core/utils"

/**
 * Receives options from the query string of the URL where SwaggerUI
 * is being served.
 */

const optionsFromQuery = () => (options) => {
  const urlSearchParams = options.queryConfigEnabled ? parseSearch() : {}

  return Object.entries(urlSearchParams).reduce((acc, [key, value]) => {
    // TODO(oliwia.rogala@smartbear.com): drop support for `config` in the next major release
    if (key === "config") {
      acc["configUrl"] = value
    } else if (key === "urls.primaryName") {
      acc[key] = value
    } else {
      acc = set(acc, key, value)
    }
    return acc
  }, {})
}

export default optionsFromQuery
/**
 * @prettier
 * Receives options from a remote URL.
 */
const makeDeferred = () => {
  const deferred = {}
  deferred.promise = new Promise((resolve, reject) => {
    deferred.resolve = resolve
    deferred.reject = reject
  })
  return deferred
}

const optionsFromURL =
  ({ url, system }) =>
  async (options) => {
    if (!url) return {}
    if (typeof system.configsActions?.getConfigByUrl !== "function") return {}
    const deferred = makeDeferred()
    const callback = (fetchedOptions) => {
      // receives null on remote URL fetch failure
      deferred.resolve(fetchedOptions)
    }

    system.configsActions.getConfigByUrl(
      {
        url,
        loadRemoteConfig: true,
        requestInterceptor: options.requestInterceptor,
        responseInterceptor: options.responseInterceptor,
      },
      callback
    )

    return deferred.promise
  }

export default optionsFromURL
