import React, { useState } from 'react';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Отслеживаем, вошел ли пользователь
  const [showForm, setShowForm] = useState(false); // Показываем форму (регистрация/вход)
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [formType, setFormType] = useState(''); // Тип формы: 'register' или 'login'

  const handleFormSubmit = (event) => {
    event.preventDefault();

    // Здесь можно добавить логику проверки/обработки данных, если потребуется
    setIsLoggedIn(true);
    setShowForm(false);
    setUsername('');
    setPassword('');
  };

  const handleLogout = () => {
    setIsLoggedIn(false); // Сбрасываем состояние
  };

  return (
    <header className="header">
      <div className="logo">My Website</div>
      <div className="right-side">
        {!isLoggedIn ? (
          <>
            <button
              className="register-button"
              onClick={() => {
                setFormType('register');
                setShowForm(true);
              }}
            >
              Зарегистрироваться
            </button>
            <button
              className="login-button"
              onClick={() => {
                setFormType('login');
                setShowForm(true);
              }}
            >
              Войти
            </button>
          </>
        ) : (
          <button className="logout-button" onClick={handleLogout}>
            Выйти
          </button>
        )}
      </div>

      {/* Модальное окно для формы */}
      {showForm && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowForm(false)}>
              &times;
            </span>
            <h2>{formType === 'register' ? 'Регистрация' : 'Войти'}</h2>
            <form onSubmit={handleFormSubmit}>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button type="submit">
                {formType === 'register' ? 'Зарегистрироваться' : 'Войти'}
              </button>
            </form>
          </div>
        </div>
      )}
    </header>
  );
}

export default Header;
