import React, { useEffect } from 'react';
import { Route, BrowserRouter as Router, Routes, useLocation, useNavigate } from 'react-router-dom';
import ArticlesPage from './components/Articles';
/*import ArticlesPage from './components/Autorization';*/ 
import DetectPage from './components/Detection';
import EventsPage from './components/EventTable';
/*import Header from './components/Header';*/
import EventsChart from './components/Graf';
/*import Search from './components/Modal';*/
import Sidebar from './components/Sidebar';
import TeamPage from './components/TeamPage';
import Welcome from './components/Welcome';


import './styles/articles.css';
import './styles/Buttons.css';
import './styles/detect.css';
import './styles/header.css';
import './styles/hello-page.css';
/*import './styles/modal.css';*/
import './styles/sidebar.css';
import './styles/styles.css';
import './styles/team.css';
import './styles/table.css';


const App = () => {
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        // Перенаправление на главную при перезагрузке
        if (location.pathname !== '/') {
            navigate('/');
        }
    }, [location, navigate]);

    return (
        <div className="app-container">
            <Sidebar />
            <div className="main-content">
                <Routes>
                    <Route path="/" element={<Welcome />} />
                    <Route path="/table" element={<EventsPage />} />
                    <Route path="/detect" element={<DetectPage />} />
                    <Route path="/graf" element={<EventsChart />} />
                    <Route path="/doc" element={<ArticlesPage />} />
                    <Route path="/team" element={<TeamPage />} />
                </Routes>
            </div>
        </div>
    );
};

export default App;
