import React, { useState } from 'react';
import axios from 'axios';

function Header() {
  const [showRegisterModal, setShowRegisterModal] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  // Обработчик регистрации
  const handleRegister = async () => {
    if (password !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }
    try {
      const response = await axios.post('http://localhost:8000/register', {
        username,
        password,
      });
      alert('User registered successfully');
      setShowRegisterModal(false);
    } catch (error) {
      console.error(error);
      alert('Error during registration');
    }
  };

  // Обработчик логина
  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8000/login', {
        username,
        password,
      });
      alert('Login successful');
      setShowLoginModal(false);
    } catch (error) {
      console.error(error);
      alert('Invalid credentials');
    }
  };

  return (
    <header className="header">
      <div className="left-side">
        <input className="search-bar" type="text" placeholder="Search" />
        <button className="search-button">
          <div className="tooltip">Search</div>
        </button>
      </div>

      <div className="right-side">
        <button className="register-button" onClick={() => setShowRegisterModal(true)}>Зарегистрироваться</button>
        <button className="login-button" onClick={() => setShowLoginModal(true)}>Войти</button>
      </div>

      {/* Модальное окно регистрации */}
      {showRegisterModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Регистрация</h2>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <input
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            />
            <button onClick={handleRegister}>Зарегистрироваться</button>
            <button onClick={() => setShowRegisterModal(false)}>Закрыть</button>
          </div>
        </div>
      )}

      {/* Модальное окно входа */}
      {showLoginModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Войти</h2>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Войти</button>
            <button onClick={() => setShowLoginModal(false)}>Закрыть</button>
          </div>
        </div>
      )}
    </header>
  );
}

export default Header;
