import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';

function Pet() {
  const [chartData, setChartData] = useState(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      const data = new Uint8Array(e.target.result);
      const workbook = XLSX.read(data, { type: 'array' });
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });

      // Преобразуйте данные в формат для графика
      const labels = jsonData.slice(1).map(row => row[0]); // Например, время
      const values = jsonData.slice(1).map(row => row[1]); // Например, активность

      setChartData({
        labels,
        datasets: [
          {
            label: 'Активность Кевина',
            data: values,
            borderColor: 'rgba(75,192,192,1)',
            backgroundColor: 'rgba(75,192,192,0.2)',
            tension: 0.4,
          },
        ],
      });
    };

    reader.readAsArrayBuffer(file);
  };

  return (
    <div className="pet-info">
      <div className="pet-photo">
        <img src="/Kevin.jpg" alt="Фото питомца" />
      </div>
      <div className="pet-details">
        <h2>Имя: Кевин</h2>
        <p><strong>Возраст:</strong> 3 года</p>
        <p><strong>Порода:</strong> Кот</p>
        <p><strong>Особенности:</strong> Очень любопытный и игривый. Любит пакеты.</p>
      </div>

      <section className="daily-activity">
        <h2>Дневник активности Кевина</h2>
        <input type="file" accept=".xlsx, .xls" onChange={handleFileUpload} />
        {chartData && (
          <Line
            data={chartData}
            options={{
              responsive: true,
              plugins: {
                legend: { display: true },
              },
            }}
          />
        )}
      </section>
    </div>
  );
}

export default Pet;
