import React, { useEffect, useState } from 'react';


const PetsPage = () => {
  const [pets, setPets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newPet, setNewPet] = useState({
    name: '',
    age: '',
    species: '',
    breed: '',
    image: null
  });
  const [error, setError] = useState('');
  const [imagePreview, setImagePreview] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchPets();
  }, []);


  const fetchPets = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/pets');

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();

      if (result.status === 'success') {
        setPets(result.data);
      } else {
        throw new Error(result.message || 'Неизвестная ошибка сервера');
      }
    } catch (error) {
      console.error('Ошибка загрузки питомцев:', error);
      setError('Не удалось загрузить список питомцев: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPet(prev => ({ ...prev, [name]: value }));
    if (error) setError('');
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Проверка типа файла
      if (!file.type.match('image.*')) {
        setError('Пожалуйста, загрузите изображение');
        return;
      }

      // Проверка размера файла (макс 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setError('Размер изображения не должен превышать 5MB');
        return;
      }

      setNewPet(prev => ({ ...prev, image: file }));

      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };


  const handleAddPet = () => {
    setShowModal(true);
    setError('');
  };


  const handleDeletePet = async () => {
    if (pets.length === 0) {
      setError('Нет питомцев для удаления');
      return;
    }

    try {
      const lastPet = pets[pets.length - 1];
      const response = await fetch(`http://localhost:8000/pets/${lastPet.id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка при удалении питомца');
      }

      await fetchPets();
      setError('');

    } catch (error) {
      console.error('Ошибка при удалении питомца:', error);
      setError(error.message);
    }
  };


  const handleSavePet = async () => {
    try {
      const formData = new FormData();
      formData.append('name', newPet.name);
      formData.append('type', newPet.species.toLowerCase()); // Приводим к нижнему регистру
      if (newPet.age) formData.append('age', newPet.age);
      if (newPet.breed) formData.append('breed', newPet.breed);
      if (newPet.image) {
        formData.append('image', newPet.image, newPet.image.name); // Добавляем имя файла
      }

      const response = await fetch('http://localhost:8000/pets', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка сервера');
      }

      // Обновляем список питомцев
      const updatedResponse = await fetch('http://localhost:8000/pets');
      const updatedData = await updatedResponse.json();
      setPets(updatedData.data || []);

      setShowModal(false);
      setNewPet({ name: '', age: '', species: '', breed: '', image: null });
      setImagePreview('');
      setError('');
    } catch (error) {
      console.error('Ошибка:', error);
      setError(error.message);
    }
  };


  // Функция для получения URL изображения
  const getImageUrl = (pet) => {
    if (!pet?.image) return null;

    try {
      // Если image уже содержит полный URL
      if (pet.image.startsWith('http')) {
        return pet.image;
      }

      // Декодируем URL и добавляем базовый адрес
      const decodedPath = decodeURIComponent(pet.image);
      return `http://localhost:8000${decodedPath}?t=${Date.now()}`;
    } catch (e) {
      console.error('Ошибка обработки URL изображения:', e);
      return null;
    }
  };


  return (
    <div className="documentation-container_table_pet">
      <h1>Ваши питомцы</h1>
      <div className="buttons-container">
        <button className="action-button add-button" onClick={handleAddPet}>
          добавить
        </button>
        <button className="action-button delete-button" onClick={handleDeletePet}>
          удалить
        </button>
      </div>

      <div className="pets-table-container">
        {pets.length > 0 ? (
          pets.map(pet => (
            <div key={pet.id} className="pet-row">

              <div className="pet-image-container">
              {pet.image ? (
                  <img 
                    src={getImageUrl(pet) || '/default-pet.jpg'}
                    alt={pet.name || 'Питомец'}
                    onError={(e) => {
                      console.error('Ошибка загрузки изображения:', e.target.src);
                      e.target.onerror = null;
                      e.target.src = '/default-pet.jpg';
                    }}
                  />
                ) : (
                  <div className="no-image">Нет фото</div>
                )}
              </div>

              <table className="pet-info-table">
                <tbody>
                  <tr>
                    <th>Имя:</th>
                    <td>{pet.name}</td>
                  </tr>
                  <tr>
                    <th>Вид:</th>
                    <td>{pet.type}</td>
                  </tr>
                  {pet.age != null && (
                    <tr>
                      <th>Возраст:</th>
                      <td>{pet.age} {getAgeWord(pet.age)}</td>
                    </tr>
                  )}
                  {pet.breed && (
                    <tr>
                      <th>Порода:</th>
                      <td>{pet.breed}</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          ))
        ) : (
          <div className="no-pets-message">
            У вас пока нет добавленных питомцев
          </div>
        )}
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Добавить нового питомца</h2>

            <div className="form-group">
              <label>Имя:</label>
              <input
                type="text"
                name="name"
                value={newPet.name}
                onChange={handleInputChange}
                placeholder="Введите имя питомца"
                required
              />
            </div>

            <div className="form-group">
              <label>Возраст:</label>
              <input
                type="number"
                name="age"
                value={newPet.age}
                onChange={handleInputChange}
                placeholder="Введите возраст"
              />
            </div>

            <div className="form-group">
              <label>Вид:</label>
              <input
                type="text"
                name="species"
                value={newPet.species}
                onChange={handleInputChange}
                placeholder="Введите 'кот' или 'собака'"
                required
              />
            </div>

            <div className="form-group">
              <label>Порода:</label>
              <input
                type="text"
                name="breed"
                value={newPet.breed}
                onChange={handleInputChange}
                placeholder="Введите породу"
              />
            </div>

            <div className="form-group">
              <label>Изображение:</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
              />
              {imagePreview && (
                <div className="image-preview">
                  <img src={imagePreview} alt="Предпросмотр" style={{ maxWidth: '100px', maxHeight: '100px', marginTop: '10px' }} />
                </div>
              )}
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="modal-buttons">
              <button 
                className="action-button cancel-button"
                onClick={() => {
                  setShowModal(false);
                  setError('');
                  setImagePreview('');
                }}
              >
                Отмена
              </button>
              <button 
                className="action-button save-button"
                onClick={handleSavePet}
              >
                Сохранить
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

function getAgeWord(age) {
  if (age % 100 >= 11 && age % 100 <= 14) {
    return 'лет';
  }
  switch (age % 10) {
    case 1: return 'год';
    case 2:
    case 3:
    case 4: return 'года';
    default: return 'лет';
  }
}

export default PetsPage;
