import React, { useState, useEffect } from 'react';

function Header() {
  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);  // Состояние для аутентификации

  // Симуляция загрузки состояния аутентификации из хранилища
  useEffect(() => {
    const authStatus = localStorage.getItem("isAuthenticated");
    setIsAuthenticated(authStatus === "true");
  }, []);

  // Функция для регистрации
  const handleRegisterSubmit = async (event) => {
    event.preventDefault();
    const userData = { username, password };

    try {
      const response = await fetch('http://localhost:8000/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        alert('User registered successfully');
        setIsAuthenticated(true);
        localStorage.setItem("isAuthenticated", "true"); // Сохраняем статус авторизации
        setShowRegister(false);
      } else {
        alert('Error during registration');
      }
    } catch (error) {
      console.error(error);
      alert('Error during registration');
    }
  };

  // Функция для входа
  const handleLoginSubmit = async (event) => {
    event.preventDefault();
    const userData = { username, password };

    try {
      const response = await fetch('http://localhost:8000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        alert('Login successful');
        setIsAuthenticated(true);
        localStorage.setItem("isAuthenticated", "true"); // Сохраняем статус авторизации
        setShowLogin(false);
      } else {
        alert('Invalid credentials');
      }
    } catch (error) {
      console.error(error);
      alert('Error during login');
    }
  };

  // Функция для выхода
  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.setItem("isAuthenticated", "false"); // Убираем статус авторизации
  };

  return (
    <header className="header">
      <div className="logo">My Website</div>
      <div className="right-side">
        {/* Показать кнопки регистрации и входа только если пользователь не авторизован */}
        {!isAuthenticated ? (
          <>
            <button className="register-button" onClick={() => setShowRegister(true)}>
              Зарегистрироваться
            </button>
            <button className="login-button" onClick={() => setShowLogin(true)}>
              Войти
            </button>
          </>
        ) : (
          // Показать кнопку выхода, если пользователь авторизован
          <button className="logout-button" onClick={handleLogout}>
            Выйти
          </button>
        )}
      </div>

      {/* Модальное окно для регистрации */}
      {showRegister && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowRegister(false)}>&times;</span>
            <h2>Регистрация</h2>
            <form onSubmit={handleRegisterSubmit}>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button type="submit">Зарегистрироваться</button>
            </form>
          </div>
        </div>
      )}

      {/* Модальное окно для авторизации */}
      {showLogin && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowLogin(false)}>&times;</span>
            <h2>Войти</h2>
            <form onSubmit={handleLoginSubmit}>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button type="submit">Войти</button>
            </form>
          </div>
        </div>
      )}
    </header>
  );
}

export default Header;
