import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function Sidebar() {
  const location = useLocation();  // Получаем текущий путь
  const navigate = useNavigate();  // Для навигации
  
  // Состояние для отслеживания, авторизован ли пользователь
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Симуляция загрузки состояния из хранилища или с сервера
  useEffect(() => {
    // Пример: проверка локального хранилища или куки, чтобы узнать, авторизован ли пользователь
    const authStatus = localStorage.getItem("isAuthenticated"); // или другой метод проверки
    setIsAuthenticated(authStatus === "true");
  }, []);

  const handleNavigation = (page) => {
    navigate(page); // Переходим на нужную страницу
  };

  const handleLogout = () => {
    // Сброс состояния аутентификации (выход)
    setIsAuthenticated(false);
    localStorage.setItem("isAuthenticated", "false"); // Убираем флаг из локального хранилища
  };

  return (
    <div className="sidebar">
      <ul>
        <li
          className={location.pathname === '/' ? 'active' : ''}
          onClick={() => handleNavigation('/')}
        >
          Главная страница
        </li>

        {/* Показать только если пользователь авторизован */}
        {isAuthenticated && (
          <>
            <li
              className={location.pathname === '/table' ? 'active' : ''}
              onClick={() => handleNavigation('/table')}
            >
              Мониторинг активности
            </li>
            <li
              className={location.pathname === '/detect' ? 'active' : ''}
              onClick={() => handleNavigation('/detect')}
            >
              Наша модель
            </li>
            <li
              className={location.pathname === '/doc' ? 'active' : ''}
              onClick={() => handleNavigation('/doc')}
            >
              Интересные статьи
            </li>
            <li
              className={location.pathname === '/team' ? 'active' : ''}
              onClick={() => handleNavigation('/team')}
            >
              Наша команда
            </li>
          </>
        )}

        {/* Показать кнопку "Выйти" только если пользователь авторизован */}
        {isAuthenticated && (
          <li onClick={handleLogout}>
            <button>Выйти</button>
          </li>
        )}
      </ul>
    </div>
  );
}

export default Sidebar;
