import React, { useEffect, useState } from 'react';


const PetsPage = () => {
  const [pets, setPets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newPet, setNewPet] = useState({
    name: '',
    age: '',
    type: '', // Изменено с species на type для согласования с бэкендом
    breed: '',
    image: null
  });
  const [error, setError] = useState('');
  const [imagePreview, setImagePreview] = useState('');

  useEffect(() => {
    fetchPets();
  }, []);

  const fetchPets = async () => {
    try {
      const response = await fetch('http://localhost:8000/pets');
      if (!response.ok) {
        throw new Error('Ошибка при загрузке данных');
      }
      const result = await response.json();
      setPets(result.data || []);
    } catch (error) {
      console.error('Ошибка:', error);
      setError('Не удалось загрузить список питомцев');
    }
  };

  const handleAddPet = () => {
    setShowModal(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPet(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewPet(prev => ({ ...prev, image: file }));

      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSavePet = async () => {
    // Валидация данных
    const lowerType = newPet.type.toLowerCase();
    if (lowerType !== 'кот' && lowerType !== 'собака') {
      setError('Пожалуйста, введите "кот" или "собака" в поле "Тип"');
      return;
    }

    if (!newPet.name.trim()) {
      setError('Пожалуйста, введите имя питомца');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('name', newPet.name);
      formData.append('type', lowerType); // Используем type вместо species
      if (newPet.age) formData.append('age', newPet.age);
      if (newPet.breed) formData.append('breed', newPet.breed);
      if (newPet.image) {
        formData.append('image', newPet.image);
      }

      const response = await fetch('http://localhost:8000/pets', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.detail || 'Ошибка сервера');
      }

      // Обновляем список питомцев
      await fetchPets();

      // Закрываем модальное окно и сбрасываем форму
      setShowModal(false);
      setNewPet({
        name: '',
        age: '',
        type: '',
        breed: '',
        image: null
      });
      setImagePreview('');
      setError('');
    } catch (error) {
      console.error('Ошибка при сохранении:', error);
      setError(error.message || 'Произошла ошибка при сохранении. Пожалуйста, попробуйте снова.');
    }
  };

  return (
    <div className="documentation-container_table">
      <h1>Ваши питомцы</h1>

      <div className="buttons-container">
        <button className="action-button add-button" onClick={handleAddPet}>
          добавить
        </button>
      </div>

      <div className="pets-table-container">
        {pets.length > 0 ? (
          pets.map(pet => (
            <div key={pet.id} className="pet-row">
              <div className="pet-image-container">
                {pet.image ? (
                  <img 
                    src={`http://localhost:8000/${pet.image}`}
                    alt={pet.name}
                    className="pet-image"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/default-pet.jpg';
                    }}
                  />
                ) : (
                  <div className="no-image">Нет фото</div>
                )}
              </div>

              <table className="pet-info-table">
                <tbody>
                  <tr>
                    <th>Имя:</th>
                    <td>{pet.name}</td>
                  </tr>
                  <tr>
                    <th>Тип:</th>
                    <td>{pet.type}</td>
                  </tr>
                  {pet.age && (
                    <tr>
                      <th>Возраст:</th>
                      <td>{pet.age} {getAgeWord(pet.age)}</td>
                    </tr>
                  )}
                  {pet.breed && (
                    <tr>
                      <th>Порода:</th>
                      <td>{pet.breed}</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          ))
        ) : (
          <p>У вас пока нет добавленных питомцев</p>
        )}
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Добавить нового питомца</h2>

            <div className="form-group">
              <label>Имя:</label>
              <input
                type="text"
                name="name"
                value={newPet.name}
                onChange={handleInputChange}
                placeholder="Введите имя питомца"
                required
              />
            </div>

            <div className="form-group">
              <label>Возраст:</label>
              <input
                type="number"
                name="age"
                value={newPet.age}
                onChange={handleInputChange}
                placeholder="Введите возраст"
              />
            </div>

            <div className="form-group">
              <label>Тип:</label>
              <input
                type="text"
                name="type"
                value={newPet.type}
                onChange={handleInputChange}
                placeholder="Введите 'кот' или 'собака'"
                required
              />
            </div>

            <div className="form-group">
              <label>Порода:</label>
              <input
                type="text"
                name="breed"
                value={newPet.breed}
                onChange={handleInputChange}
                placeholder="Введите породу"
              />
            </div>

            <div className="form-group">
              <label>Изображение:</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
              />
              {imagePreview && (
                <div className="image-preview">
                  <img 
                    src={imagePreview} 
                    alt="Предпросмотр" 
                    style={{ 
                      maxWidth: '100px', 
                      maxHeight: '100px', 
                      marginTop: '10px' 
                    }} 
                  />
                </div>
              )}
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="modal-buttons">
              <button 
                className="action-button cancel-button"
                onClick={() => {
                  setShowModal(false);
                  setError('');
                  setImagePreview('');
                }}
              >
                Отмена
              </button>
              <button 
                className="action-button save-button"
                onClick={handleSavePet}
              >
                Сохранить
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Вспомогательная функция для склонения слова "год"
function getAgeWord(age) {
  if (age % 100 >= 11 && age % 100 <= 14) {
    return 'лет';
  }
  switch (age % 10) {
    case 1: return 'год';
    case 2:
    case 3:
    case 4: return 'года';
    default: return 'лет';
  }
}

export default PetsPage;
