import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EventsPage = () => {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await axios.get('http://localhost:8000/events');
                const events = response.data;

                // Группировка данных по уникальному имени
                const groupedData = events.reduce((acc, event) => {
                    const { name, event_type_id, created_at } = event;

                    if (!acc[name]) {
                        acc[name] = { name, events: { 1: 0, 2: 0, 3: 0, 4: 0 } }; // Инициализируем события
                    }

                    // Рассчитываем время (количество событий -> часы)
                    if (event_type_id in acc[name].events) {
                        acc[name].events[event_type_id] += 1; // Считаем количество событий каждого типа
                    }

                    return acc;
                }, {});

                // Преобразуем данные в массив для рендера
                const processedData = Object.values(groupedData).map((group) => {
                    const eventDurations = {};
                    Object.keys(group.events).forEach((eventTypeId) => {
                        const totalHours = group.events[eventTypeId]; // Суммируем в часы
                        eventDurations[eventTypeId] = totalHours.toFixed(2); // Округляем
                    });
                    return { ...group, events: eventDurations };
                });

                setData(processedData);
            } catch (error) {
                console.error("Ошибка при получении данных:", error);
            }
        };

        fetchEvents();
    }, []);

    return (
        <div>
            <h1>События по питомцам</h1>
            {data.length > 0 ? (
                data.map((group, index) => (
                    <div key={index} style={{ marginBottom: '2rem' }}>
                        {/* Имя питомца */}
                        <h2>{group.name}</h2>
                        {/* Таблица событий */}
                        <table border="1" style={{ width: '100%', textAlign: 'left', borderCollapse: 'collapse' }}>
                            <thead>
                                <tr>
                                    <th>Тип события</th>
                                    <th>Сумма времени (часы)</th>
                                </tr>
                            </thead>
                            <tbody>
                                {Object.entries(group.events).map(([eventTypeId, totalHours]) => (
                                    <tr key={eventTypeId}>
                                        <td>{eventTypeId}</td>
                                        <td>{totalHours}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                ))
            ) : (
                <p>Данные отсутствуют</p>
            )}
        </div>
    );
};

export default EventsPage;
