import React from 'react';

function Welcome() {
  // Массив с фотографиями и описаниями
  const photoData = [
    {
      id: 1,
      imgSrc: '/vova.jpg',
      alt: 'Фото 1',
      description: 'Это изображение иллюстрирует ключевую часть проекта. Мы используем современные подходы в разработке интерфейсов, чтобы создать качественный пользовательский опыт.',
    },
    {
      id: 2,
      imgSrc: '/vasya.jpg',
      alt: 'Фото 2',
      description: 'Вторая фотография демонстрирует важный аспект визуализации данных в нашем проекте. Мы обеспечиваем простоту взаимодействия с пользователем и доступность информации.',
    },
    // Можете добавить больше объектов для дополнительных фотографий
  ];

  return (
    <div className='helloPage'>
      <h1>Приветственная страница</h1>
      
      {/* Блок с описанием проекта */}
      <section className='projectDescription'>
        <h2>О проекте</h2>
        <p>
          Продуктом проекта является комплексная система видеонаблюдения и аналитики для мониторинга домашних животных (кошек и собак). Система включает в себя выполнение следующих функций:
        </p>
        <ul>
          <li>Отслеживание активности питомца;</li>
          <li>Подключение к умной кормушке. Пользователь может настроить расписание кормления, а также определить необходимое количество корма;</li>
          <li>Дрессировка через отрицательное подкрепление. (Пример: кошка прыгает на диван, колонка издает резкий, неприятный для кошки звук);</li>
          <li>Определение аномальной активности и питания питомца. Если питомец начал потреблять меньше пищи или вести себя неестественным образом, нейросеть определяет это и уведомляет пользователя;</li>
        </ul>
      </section>

      {/* Блок с фотографиями в шахматном порядке */}
      <section className='photoGrid'>
        {photoData.map((photo, index) => (
          <div className={`photoDescription ${index % 2 === 0 ? 'left' : 'right'}`} key={photo.id}>
            <img src={photo.imgSrc} alt={photo.alt} className='photo'/>
            <p className='description'>
              {photo.description}
            </p>
          </div>
        ))}
      </section>
    </div>
  );
}

export default Welcome;
