import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import ArticlesPage from './components/Articles';
import Header from './components/Header';
import DetectPage from './components/Detection';
import EventsPage from './components/EventTable';
import Sidebar from './components/Sidebar';
import TeamPage from './components/TeamPage';
import Welcome from './components/Welcome';
import LoginPage from './components/LoginPage'; // Страница входа или заглушка

import './styles/articles.css';
import './styles/Buttons.css';
import './styles/detect.css';
import './styles/header.css';
import './styles/hello-page.css';
import './styles/sidebar.css';
import './styles/styles.css';
import './styles/team.css';
import './styles/table.css';

const ProtectedRoute = ({ isAuthenticated, children }) => {
    return isAuthenticated ? children : <Navigate to="/login" replace />;
};

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    const handleLogin = () => {
        setIsAuthenticated(true); // Вызывается после успешной регистрации/входа
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
    };

    return (
        <div className="app-container">
            <Router>
                <Header onLogout={handleLogout} isAuthenticated={isAuthenticated} />
                <Sidebar />
                <div className="main-content">
                    <Routes>
                        {/* Незащищённые маршруты */}
                        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />

                        {/* Защищённые маршруты */}
                        <Route
                            path="/"
                            element={
                                <ProtectedRoute isAuthenticated={isAuthenticated}>
                                    <Welcome />
                                </ProtectedRoute>
                            }
                        />
                        <Route
                            path="/table"
                            element={
                                <ProtectedRoute isAuthenticated={isAuthenticated}>
                                    <EventsPage />
                                </ProtectedRoute>
                            }
                        />
                        <Route
                            path="/detect"
                            element={
                                <ProtectedRoute isAuthenticated={isAuthenticated}>
                                    <DetectPage />
                                </ProtectedRoute>
                            }
                        />
                        <Route
                            path="/doc"
                            element={
                                <ProtectedRoute isAuthenticated={isAuthenticated}>
                                    <ArticlesPage />
                                </ProtectedRoute>
                            }
                        />
                        <Route
                            path="/team"
                            element={
                                <ProtectedRoute isAuthenticated={isAuthenticated}>
                                    <TeamPage />
                                </ProtectedRoute>
                            }
                        />
                    </Routes>
                </div>
            </Router>
        </div>
    );
};

export default App;
