import axios from 'axios';
import React, { useEffect, useState } from 'react';

const EventsPage = () => {
  const [data, setData] = useState([]);
  const [showTrackingOptions, setShowTrackingOptions] = useState(false);
  const [showResultsOptions, setShowResultsOptions] = useState(false);
  const [trackingTime, setTrackingTime] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);
  const [endTime, setEndTime] = useState(null);

  // Словарь для отображения типов событий в текстовом виде
  const eventTypeText = {
    1: 'The animal is actively moving',
    2: 'The animal is at peace or relax',
    3: 'The animal eats',
    4: 'The animal goes to the toilet',
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:8000/events');
        const events = response.data;

        // Группируем события по уникальным именам
        const groupedData = events.reduce((acc, event) => {
          const { pets_id, events_id } = event;

          // Если еще нет группы для такого имени, создаем пустую
          if (!acc[pets_id]) {
            acc[pets_id] = {
              pets_id,
              events: {
                1: 0,
                2: 0,
                3: 0,
                4: 0
              }
            };
          }

          // Прибавляем 1 секунду для каждого события типа
          acc[pets_id].events[events_id] += 1; // Добавляем одну секунду

          return acc;
        }, {});

        const processedData = Object.values(groupedData).map(group => ({
          pets_id: group.pets_id,
          events: Object.fromEntries(
            Object.entries(group.events).map(([eventTypeId, seconds]) => [
              eventTypeText[eventTypeId], Math.floor(seconds)
            ])
          )
        }));

        // Сохраняем данные для отображения
        setData(processedData);
      } catch (error) {
        console.error('Ошибка при получении данных:', error);
      }
    };

    fetchEvents();
  }, []);

  useEffect(() => {
    const savedTracking = localStorage.getItem('petTracking');
    if (savedTracking) {
      const { endTime: savedEndTime, duration } = JSON.parse(savedTracking);
      const endTimeDate = new Date(savedEndTime);
      const now = new Date();

      if (endTimeDate > now) {
        const secondsLeft = Math.floor((endTimeDate - now) / 1000);
        setEndTime(endTimeDate);
        setTimeLeft(secondsLeft);
        setTrackingTime(duration);
      } else {
        localStorage.removeItem('petTracking');
      }
    }
  }, []);

  // Запуск таймера
  useEffect(() => {
    if (!endTime) return;

    const timer = setInterval(() => {
      const now = new Date();
      const secondsLeft = Math.floor((endTime - now) / 1000);

      if (secondsLeft <= 0) {
        clearInterval(timer);
        setTimeLeft(null);
        setTrackingTime(null);
        setEndTime(null);
        localStorage.removeItem('petTracking');
      } else {
        setTimeLeft(secondsLeft);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [endTime]);

  // Функция для записи в лог
  const logActivity = async () => {
    const now = new Date();
    const recordDate = now.toISOString().slice(0, 19).replace('T', ' ');

    try {
      await axios.post('http://localhost:8000/activity_log');
      console.log('Запись в лог успешно добавлена');
    } catch (error) {
      console.error('Ошибка при записи в лог:', error);
    }
  };

  const handleStartTracking = (duration) => {
    console.log(`Запуск отслеживания на ${duration}`);
    setShowTrackingOptions(false);

    let seconds;
    switch(duration) {
      case '2 часа':
        seconds = 2 * 60 * 60 - 1; // 1 час 59 мин 59 сек
        break;
      case '10 часов':
        seconds = 10 * 60 * 60 - 1;
        break;
      case '1 день':
        seconds = 24 * 60 * 60 - 1; // 23 ч 59 мин 59 сек
        break;
      default:
        seconds = 0;
    }

    setTrackingTime(duration);
    setTimeLeft(seconds);
    logActivity();
  };

  const handleViewResults = (period) => {
    console.log(`Просмотр результатов за ${period}`);
    setShowResultsOptions(false);
    // Здесь можно добавить вызов API для получения результатов
  };

  const toggleTrackingOptions = () => {
    setShowTrackingOptions(!showTrackingOptions);
    setShowResultsOptions(false);
  };

  const toggleResultsOptions = () => {
    setShowResultsOptions(!showResultsOptions);
    setShowTrackingOptions(false);
  };

  const formatTime = (seconds) => {
    if (seconds === null) return null;

    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    return `${hours.toString().padStart(2, '0')} ч ${minutes.toString().padStart(2, '0')} мин ${secs.toString().padStart(2, '0')} сек`;
  };

  return (
    <div className="documentation-container_table">
      <h1>Активность ваших питомцев</h1>

      <div className="buttons-container_active">
        <div className="dropdown">
          <button className="track-button" onClick={toggleTrackingOptions}>
            Запустить отслеживание
          </button>
          {showTrackingOptions && (
            <div className="dropdown-content">
              <div onClick={() => handleStartTracking('2 часа')}>2 часа</div>
              <div onClick={() => handleStartTracking('10 часов')}>10 часов</div>
              <div onClick={() => handleStartTracking('1 день')}>1 день</div>
            </div>
          )}
        </div>

        <div className="dropdown">
            <button className="results-button" onClick={toggleResultsOptions}>
              Посмотреть результаты
            </button>
            {showResultsOptions && (
              <div className="dropdown-content">
                <div onClick={() => handleViewResults('текущие результаты')}>Текущие результаты</div>
                <div onClick={() => handleViewResults('1 день')}>1 день</div>
                <div onClick={() => handleViewResults('1 неделя')}>1 неделя</div>
              </div>
            )}
        </div>
      </div>

      {timeLeft !== null && (
        <div className="tracking-info">
          <p>До конца отслеживания осталось: {formatTime(timeLeft)}</p>
        </div>
      )}

      {data.length > 0 ? (
        data.map((group, index) => (
          <div key={index} className="table-container">
            {/* Выводим изображение питомца слева */}
            <div className="pet-photo">
              <img
                src={`/${group.pets_id}.jpg`}
                alt={group.pets_id}
                className="pet-photo-img"
              />
            </div>
            <div className="event-data">
              {/* Выводим имя питомца */}
              <h2>{group.pets_id}</h2>
              {/* Таблица событий */}
              <table className="event-table">
                <thead>
                  <tr>
                    <th>Тип события</th>
                    <th>Сумма времени (минуты)</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(group.events).map(([eventTypeName, totalMinutes]) => (
                    <tr key={eventTypeName}>
                      <td>{eventTypeName}</td>
                      <td>{totalMinutes}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ))
      ) : (
        <p>Данные отсутствуют</p>
      )}
    </div>
  );
};

export default EventsPage;
