import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function Sidebar({ isLoggedIn }) {
    const location = useLocation();  // Получаем текущий путь
    const navigate = useNavigate();  // Для навигации

    const handleNavigation = (page) => {
        navigate(page); // Переходим на нужную страницу
    };

    if (!isLoggedIn) {
        return null; // Если не авторизован, ничего не показываем
    }

    return (
        <div className="sidebar">
            <ul>
                <li
                    className={location.pathname === '/' ? 'active' : ''}
                    onClick={() => handleNavigation('/')}
                >
                    Главная страница
                </li>
                <li
                    className={location.pathname === '/table' ? 'active' : ''}
                    onClick={() => handleNavigation('/table')}
                >
                    Мониторинг активности
                </li>
                <li
                    className={location.pathname === '/detect' ? 'active' : ''}
                    onClick={() => handleNavigation('/detect')}
                >
                    Наша модель
                </li>
                <li
                    className={location.pathname === '/doc' ? 'active' : ''}
                    onClick={() => handleNavigation('/doc')}
                >
                    Интересные статьи
                </li>
                <li
                    className={location.pathname === '/team' ? 'active' : ''}
                    onClick={() => handleNavigation('/team')}
                >
                    Наша команда
                </li>
            </ul>
        </div>
    );
}

export default Sidebar;
