import React, { useState } from 'react';

const Search = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [suggestions, setSuggestions] = useState([]);
    const [loading, setLoading] = useState(false);

    const highlightText = (text, query) => {
        const regex = new RegExp(`(${query})`, 'gi');
        return text.split(' ').map((word, index) => {
            if (regex.test(word)) {
                return (
                    <span key={index} style={{ backgroundColor: 'yellow' }}>
                        {word}
                    </span>
                );
            } else {
                return word + ' ';
            }
        });
    };

    const filterSuggestions = (suggestions) => {
        const uniqueSuggestions = [];
        const seen = new Set();

        suggestions.forEach(suggestion => {
            if (!seen.has(suggestion.suggestion)) {
                uniqueSuggestions.push(suggestion);
                seen.add(suggestion.suggestion);
            }
        });

        return uniqueSuggestions;
    };

    const handleSearch = async () => {
        if (!query) return;

        setLoading(true);
        try {
            const response = await fetch('http://localhost:8010/search', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query: query }),
            });

            if (response.ok) {
                const data = await response.json();
                setResults(data.results);
                setSuggestions(data.suggestions);
            } else {
                console.error('Ошибка при поиске:', response.statusText);
            }
        } catch (error) {
            console.error('Ошибка при выполнении запроса:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="code">
            <div className="white-stripe">
                <h1>Поиск по сайту</h1>
                <input
                    type="text"
                    placeholder="Введите поисковый запрос"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <button onClick={handleSearch} disabled={loading}>
                    {loading ? 'Поиск...' : 'Искать'}
                </button>

                <div>
                    {results.length > 0 ? (
                        <ul>
                            {results.map((result, index) => (
                                <li key={index}>
                                    <a href={result.url} target="_blank" rel="noopener noreferrer">
                                        {result.url}
                                    </a>
                                    {/* Подсветка совпадений в контексте результатов поиска */}
                                    <p>{highlightText(result.context, query)}</p>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>Результаты не найдены</p>
                    )}

                    {suggestions.length > 0 && (
                        <div>
                            <h3>Возможно, вы имели в виду:</h3>
                            <ul>
                                {filterSuggestions(suggestions).map((suggestion, index) => (
                                    <li key={index}>
                                        <a href={suggestion.url} target="_blank" rel="noopener noreferrer">
                                            {suggestion.suggestion}
                                        </a>
                                        <p>{highlightText(suggestion.context, query)}</p>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Search;
