import React, { useState } from 'react';

function Header() {
  const [showRegister, setShowRegister] = useState(false); // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏
  const [showLogin, setShowLogin] = useState(false); // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏
  const [username, setUsername] = useState(''); // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –≤–≤–µ–¥–µ–Ω–Ω–æ–≥–æ –∏–º–µ–Ω–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  const [password, setPassword] = useState(''); // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –ø–∞—Ä–æ–ª—è
  const [query, setQuery] = useState(''); // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –≤–≤–æ–¥–∞ –ø–æ–∏—Å–∫–æ–≤–æ–≥–æ —Ç–µ–∫—Å—Ç–∞

  const handleRegisterSubmit = async (event) => {
    event.preventDefault();
    const userData = { username, password };

    try {
      const response = await fetch('http://localhost:8000/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        alert('User registered successfully');
        setShowRegister(false); // –ó–∞–∫—Ä—ã—Ç—å –æ–∫–Ω–æ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏
      } else {
        alert('Error during registration');
      }
    } catch (error) {
      console.error(error);
      alert('Error during registration');
    }
  };

  const handleLoginSubmit = async (event) => {
    event.preventDefault();
    const userData = { username, password };

    try {
      const response = await fetch('http://localhost:8000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        alert('Login successful');
        setShowLogin(false); // –ó–∞–∫—Ä—ã—Ç—å –æ–∫–Ω–æ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏
      } else {
        alert('Invalid credentials');
      }
    } catch (error) {
      console.error(error);
      alert('Error during login');
    }
  };

  const handleSearchChange = (event) => {
    setQuery(event.target.value);
  };

  const handleSearch = () => {
    // –°–Ω–∞—á–∞–ª–∞ —Å–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—Å–µ –ø–æ–¥—Å–≤–µ—Ç–∫–∏
    const highlightedElements = document.querySelectorAll('.highlight');
    highlightedElements.forEach((element) => {
      element.classList.remove('highlight');
    });

    if (query) {
      const regExp = new RegExp(query, 'gi');
      const elements = document.body.querySelectorAll('*');

      elements.forEach((element) => {
        if (element.children.length === 0 && element.textContent.match(regExp)) {
          const newHtml = element.innerHTML.replace(
            regExp,
            (match) => `<span class="highlight">${match}</span>`
          );
          element.innerHTML = newHtml;
        }
      });
    }
  };

  return (
    <header className="header">
      <div className="left-side">
        <input
          className="search-bar"
          type="text"
          placeholder="–ü–æ–∏—Å–∫..."
          value={query}
          onChange={handleSearchChange}
        />
        <button className="search-button" onClick={handleSearch}>
        <img src=".üîç.png" alt="Search icon" className="icon" />
        </button>
      </div>

      <div className="right-side">
        <button className="register-button" onClick={() => setShowRegister(true)}>
          –ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è
        </button>
        <button className="login-button" onClick={() => setShowLogin(true)}>
          –í–æ–π—Ç–∏
        </button>
      </div>

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –¥–ª—è —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏ */}
      {showRegister && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowRegister(false)}>
              &times;
            </span>
            <h2>–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è</h2>
            <form onSubmit={handleRegisterSubmit}>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button type="submit">–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è</button>
            </form>
          </div>
        </div>
      )}

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –¥–ª—è –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏ */}
      {showLogin && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowLogin(false)}>
              &times;
            </span>
            <h2>–í–æ–π—Ç–∏</h2>
            <form onSubmit={handleLoginSubmit}>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button type="submit">–í–æ–π—Ç–∏</button>
            </form>
          </div>
        </div>
      )}
    </header>
  );
}

export default Header;
