import React, { useEffect, useState } from 'react';
/*import Slider from './Slider';*/

const Detection = () => {
    const [posts, setPosts] = useState([]);
    const [displayedPosts, setDisplayedPosts] = useState(1);
    const quantityPostsMax = 5;

    const handlePostsChange = (newCount) => {
        setDisplayedPosts(newCount);
    };

    const [selectedImage, setSelectedImage] = useState(null);
    const [invertedImage, setInvertedImage] = useState(null);

    const handleImageChange = (event) => {
        setSelectedImage(event.target.files[0]);
    };

    const handleImageUpload = async () => {
        if (!selectedImage) return;

        const formData = new FormData();
        formData.append('file', selectedImage);

        try {
            const response = await fetch('http://localhost:8000/invert-image', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('Ошибка при загрузке изображения');
            }

            const blob = await response.blob();
            const imageUrl = URL.createObjectURL(blob);
            setInvertedImage(imageUrl);
        } catch (error) {
            console.error('Ошибка при загрузке изображения:', error);
        }
    };

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await fetch('http://localhost:8000/posts');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setPosts(data);
            } catch (error) {
                console.error('Ошибка при загрузке постов:', error);
            }
        };

        fetchPosts();
    }, []);

    return (
        <div>
            <h3 id="posts" className="hover-header">Посты</h3>
            <Slider quantityPostsMax={Math.min(quantityPostsMax, posts.length)} quantityPostsStart={displayedPosts} quantityPostsChange={handlePostsChange} />
            <ul>
                {posts.slice(0, displayedPosts).map((post) => (
                    <li key={post.id}>
                        <h4>{post.title}</h4>
                        <p>{post.body}</p>
                    </li>
                ))}
            </ul>

            <h3>Загрузить изображение для инвертирования</h3>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            <button onClick={handleImageUpload}>Инвертировать изображение</button>
            {invertedImage && (
                <div>
                    <h4>Инвертированное изображение:</h4>
                    <img src={invertedImage} alt="Inverted" />
                </div>
            )}
        </div>
    );
};

export default Detection;
