import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Sidebar.css';  // Если вы хотите использовать стили для активной кнопки

function Sidebar() {
    const [activePage, setActivePage] = useState('/'); // Храним активную страницу
    const navigate = useNavigate();
    const location = useLocation(); // Используем для определения текущего маршрута

    const handleNavigation = (page) => {
        setActivePage(page); // Обновляем активную страницу
        navigate(page); // Перенаправляем на нужный маршрут
    };

    return (
        <div className="sidebar">
            <ul>
                <li
                    className={activePage === '/' || location.pathname === '/' ? 'active' : ''}
                    onClick={() => handleNavigation('/')}
                >
                    Главная
                </li>
                <li
                    className={activePage === '/pet' || location.pathname === '/pet' ? 'active' : ''}
                    onClick={() => handleNavigation('/pet')}
                >
                    Личный кабинет
                </li>
                <li
                    className={activePage === '/doc' || location.pathname === '/doc' ? 'active' : ''}
                    onClick={() => handleNavigation('/doc')}
                >
                    Статьи
                </li>
                <li
                    className={activePage === '/detect' || location.pathname === '/detect' ? 'active' : ''}
                    onClick={() => handleNavigation('/detect')}
                >
                    Детекция
                </li>
                <li
                    className={activePage === '/eventsChart' || location.pathname === '/eventsChart' ? 'active' : ''}
                    onClick={() => handleNavigation('/eventsChart')}
                >
                    График
                </li>
                <li
                    className={activePage === '/search' || location.pathname === '/search' ? 'active' : ''}
                    onClick={() => handleNavigation('/search')}
                >
                    Поиск
                </li>
                <li
                    className={activePage === '/api' || location.pathname === '/api' ? 'active' : ''}
                    onClick={() => handleNavigation('/api')}
                >
                    API
                </li>
            </ul>
        </div>
    );
}

export default Sidebar;
