import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EventsPage = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:8000/events');
        const events = response.data;

        // Группируем события по уникальным именам
        const groupedData = events.reduce((acc, event) => {
          const { name, event_type_id, created_at } = event;

          // Если еще нет группы для такого имени, создаем пустую
          if (!acc[name]) {
            acc[name] = {
              name,
              events: {
                1: 0,
                2: 0,
                3: 0,
                4: 0
              }
            };
          }

          // Прибавляем время для каждого типа события
          const eventDate = new Date(created_at);
          const hours = eventDate.getHours() + eventDate.getMinutes() / 60; // Переводим время в часы
          acc[name].events[event_type_id] += hours;

          return acc;
        }, {});

        // Преобразуем в массив для рендера
        const processedData = Object.values(groupedData).map(group => ({
          name: group.name,
          events: group.events
        }));

        // Сохраняем в состояние для отображения
        setData(processedData);
      } catch (error) {
        console.error('Ошибка при получении данных:', error);
      }
    };

    fetchEvents();
  }, []);

  return (
    <div>
      <h1>События по питомцам</h1>
      {data.length > 0 ? (
        data.map((group, index) => (
          <div key={index} style={{ marginBottom: '2rem' }}>
            {/* Выводим имя питомца */}
            <h2>{group.name}</h2>
            {/* Таблица событий */}
            <table border="1" style={{ width: '100%', textAlign: 'left', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th>Тип события</th>
                  <th>Сумма времени (часы)</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(group.events).map(([eventTypeId, totalHours]) => (
                  <tr key={eventTypeId}>
                    <td>{eventTypeId}</td>
                    <td>{totalHours.toFixed(2)} часов</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))
      ) : (
        <p>Данные отсутствуют</p>
      )}
    </div>
  );
};

export default EventsPage;
