import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Регистрация компонентов Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function Pet() {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const loadExcelFile = async () => {
      try {
        const response = await fetch('/Kevin.xlsx');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.arrayBuffer();

        const workbook = XLSX.read(data, { type: 'array' });
        const sheetName = workbook.SheetNames[0];
        const sheet = workbook.Sheets[sheetName];
        const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });

        const labels = jsonData.slice(1).map(row => row[0]); // Время
        const values = jsonData.slice(1).map(row => row[1]); // Процент активности

        setChartData({
          labels,
          datasets: [
            {
              label: 'Процент активности Кевина',
              data: values,
              borderColor: (ctx) => {
                const chart = ctx.chart;
                const { ctx: canvasCtx, chartArea } = chart;

                if (!chartArea) {
                  return 'rgba(75, 192, 192, 1)';
                }

                const gradient = canvasCtx.createLinearGradient(
                  7,
                  chartArea.top,
                  0,
                  chartArea.bottom
                );

                gradient.addColorStop(0, 'red'); // Высокая активность
                gradient.addColorStop(1, 'blue'); // Низкая активность

                return gradient;
              },
              backgroundColor: 'white',
              tension: 0.4,
            },
          ],
        });
      } catch (error) {
        console.error('Ошибка при загрузке файла:', error);
      }
    };

    loadExcelFile();
  }, []);

  return (
    <div className="pet-info">
      <div className="pet-photo">
        <img src="/Kevin.jpg" alt="Фото питомца" />
      </div>
      <div className="pet-details">
        <h2>Имя: Кевин</h2>
        <p><strong>Возраст:</strong> 3 года</p>
        <p><strong>Порода:</strong> Кот</p>
        <p><strong>Особенности:</strong> Очень любопытный и игривый. Любит пакеты.</p>
      </div>

      <section className="daily-activity">
        <h2>Дневник активности Кевина</h2>
        {chartData ? (
          <Line
            data={chartData}
            options={{
              responsive: false,
              maintainAspectRatio: false, // Поддержание пропорций
              plugins: {
                legend: { display: true },
              },
              scales: {
                x: { title: { display: true, text: 'Время' } },
                y: { title: { display: true, text: 'Процент активности' } },
              },
            }}
            width={600} // Установите фиксированную ширину
            height={300} // Установите фиксированную высоту
          />
        ) : (
          <p>Загрузка данных...</p>
        )}
      </section>
    </div>
  );
}

export default Pet;
