import React, { useState, useEffect, useRef } from "react";
import './Buttons.css'

const EventsChart = () => {
    const [homeId, setHomeId] = useState(null);
    const [data, setData] = useState([]);
    const canvasRef = useRef(null);
    const chartHeight = 100;
    const barWidth = 30;

    // Функция для получения данных
    const fetchData = async (homeId) => {
        try {
            const url = homeId ? `http://localhost:8000/events?id_home=${homeId}` : "http://localhost:8000/events";
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setData(data);
        } catch (error) {
            console.error("Ошибка при загрузке данных", error);
        }
    };

    const renderChart = (data) => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        if (data.length === 0) return;

        const maxWidth = 1000;
        const canvasWidth = Math.min(data.length * barWidth, maxWidth);
        canvas.width = canvasWidth;
        canvas.height = chartHeight + 80;

        ctx.clearRect(0, 0, canvas.width, canvas.height);

        const gradient = ctx.createLinearGradient(0, 0, canvas.width, 0);
        gradient.addColorStop(0, "#D9A6F5");
        gradient.addColorStop(1, "#FFFB7E");

        const colors = {
            1: "#D9A6F5",
            2: "#FFFB7E",
            3: "#4A90E2",
            0: "#F7A8B8",
        };

        data.forEach((event, index) => {
            const x = index * barWidth;
            const color = colors[event.event_type_id] || "#F7A8B8";

            ctx.fillStyle = gradient;
            ctx.fillRect(x, 0, barWidth, chartHeight);

            ctx.fillStyle = color;
            ctx.fillRect(x, 0, barWidth, chartHeight);

            ctx.strokeStyle = "white";
            ctx.lineWidth = 1;
            ctx.strokeRect(x, 0, barWidth, chartHeight);

            if (index % 5 === 0) {
                ctx.fillStyle = "black";
                ctx.font = "12px Arial";
                ctx.textAlign = "center";
                const time = new Date(event.created_at).toLocaleTimeString();
                ctx.fillText(time, x + barWidth / 2, chartHeight + 20);
            }
        });

        ctx.fillStyle = "black";
        ctx.font = "14px Arial";
        ctx.textAlign = "left";
        ctx.fillText("Цвета событий:", 10, canvas.height - 30);
        ctx.fillStyle = "#070000";
        ctx.fillText("Сиреневый: Активные действия", 120, canvas.height - 15);
        ctx.fillStyle = "#070000FF";
        ctx.fillText("Желтый: Спокойствие", 400, canvas.height - 15);
        ctx.fillStyle = "#070000FF";
        ctx.fillText("Синий: Туалет", 600, canvas.height - 15);
        ctx.fillStyle = "#070000FF";
        ctx.fillText("Розовый: Еда", 800, canvas.height - 15);
    };

    useEffect(() => {
        if (homeId) {
            fetchData(homeId);
        }
    }, [homeId]);

    useEffect(() => {
        if (data.length > 0) {
            renderChart(data);
        }
    }, [data]);

    return (
        <div style={{ backgroundColor: "#f4f4f4", padding: "20px", borderRadius: "10px" }}>
            <h2 style={{ textAlign: "center" }}>Выберите комнату для отображения графика</h2>

            <div style={{ textAlign: "center", marginBottom: "20px" }}>
                <button onClick={() => setHomeId(101)}>BedRoom</button>
                <button onClick={() => setHomeId(102)}>Kitchen</button>
            </div>

            {homeId && (
                <div style={{ overflowX: "auto", marginTop: "30px" }}>
                    <h3 style={{ textAlign: "center" }}>Полосовой график событий</h3>
                    <canvas ref={canvasRef}></canvas>
                </div>
            )}
        </div>
    );
};

export default EventsChart;
