import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Регистрация компонентов Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function Pet() {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const loadExcelFile = async () => {
      try {
        console.log('Начало загрузки файла');
        const response = await fetch('/public/Kevin.xlsx');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.arrayBuffer();
        console.log('Файл успешно загружен');

        const workbook = XLSX.read(data, { type: 'array' });
        console.log('Файл прочитан библиотекой XLSX');

        const sheetName = workbook.SheetNames[0];
        const sheet = workbook.Sheets[sheetName];
        const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });
        console.log('Данные из файла:', jsonData);

        const labels = jsonData.slice(1).map(row => row[0]); // Время
        const values = jsonData.slice(1).map(row => row[1]); // Процент активности

        setChartData({
          labels,
          datasets: [
            {
              label: 'Процент активности Кевина',
              data: values,
              borderColor: 'rgba(75,192,192,1)',
              backgroundColor: 'rgba(75,192,192,0.2)',
              tension: 0.4,
            },
          ],
        });
        console.log('Данные для графика успешно сформированы');
      } catch (error) {
        console.error('Ошибка при загрузке файла:', error);
      }
    };

    loadExcelFile();
  }, []);

  return (
    <div className="pet-info">
      <div className="pet-photo">
        <img src="/Kevin.jpg" alt="Фото питомца" />
      </div>
      <div className="pet-details">
        <h2>Имя: Кевин</h2>
        <p><strong>Возраст:</strong> 3 года</p>
        <p><strong>Порода:</strong> Кот</p>
        <p><strong>Особенности:</strong> Очень любопытный и игривый. Любит пакеты.</p>
      </div>

      <section className="daily-activity">
        <h2>Дневник активности Кевина</h2>
        {chartData ? (
          <Line
            data={chartData}
            options={{
              responsive: true,
              plugins: {
                legend: { display: true },
              },
              scales: {
                x: { title: { display: true, text: 'Время' } },
                y: { title: { display: true, text: 'Процент активности' } },
              },
            }}
          />
        ) : (
          <p>Загрузка данных...</p>
        )}
      </section>
    </div>
  );
}

export default Pet;
