import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import ArticlesPage from './components/Articles';
import DetectPage from './components/Detection';
import EventsPage from './components/EventTable';
import TeamPage from './components/TeamPage';
import Welcome from './components/Welcome';

import './styles/styles.css';  // Пример подключения стилей

const App = () => {
  return (
    <Router> {/* Один Router оборачивает все приложение */}
      <div className="app-container">
        <Header />
        <Sidebar /> {/* Sidebar будет работать корректно, так как он обернут в Router */}
        <div className="main-content">
          <Routes>
            <Route path="/" element={<Welcome />} />
            <Route path="/table" element={<EventsPage />} />
            <Route path="/detect" element={<DetectPage />} />
            <Route path="/doc" element={<ArticlesPage />} />
            <Route path="/team" element={<TeamPage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
