import React, { useEffect, useState } from 'react';


const PetsPage = () => {
  const [pets, setPets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newPet, setNewPet] = useState({
    name: '',
    age: '',
    species: '',
    breed: '',
    image: null
  });
  const [error, setError] = useState('');
  const [imagePreview, setImagePreview] = useState('');

  // Обработчик изменения полей ввода
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPet(prev => ({ ...prev, [name]: value }));
  };


  // Обработчик изменения изображения
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewPet(prev => ({ ...prev, image: file }));

      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };


  useEffect(() => {
    fetchPets();
  }, []);

  const fetchPets = async () => {
    try {
      const response = await fetch('http://localhost:8000/pets');

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();

      if (result.status === 'success') {
        setPets(result.data);
      } else {
        throw new Error(result.message || 'Неизвестная ошибка сервера');
      }

    } catch (error) {
      console.error('Ошибка загрузки питомцев:', error);
      setError('Не удалось загрузить список питомцев: ' + error.message);
    }
  };

  const handleAddPet = () => {
    setShowModal(true);
  };

  const handleDeletePet = async () => {
    if (pets.length === 0) {
      setError('Нет питомцев для удаления');
      return;
    }

    try {
      const lastPet = pets[pets.length - 1];
      const response = await fetch(`http://localhost:8000/pets/${lastPet.id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка при удалении питомца');
      }

      await fetchPets();
      setError('');

    } catch (error) {
      console.error('Ошибка при удалении питомца:', error);
      setError(error.message);
    }
  };


  const handleSavePet = async () => {
    const lowerSpecies = newPet.species.toLowerCase();
    if (lowerSpecies !== 'кот' && lowerSpecies !== 'собака') {
      setError('Пожалуйста, введите "кот" или "собака" в поле "Вид"');
      return;
    }

    if (!newPet.name) {
      setError('Пожалуйста, введите имя питомца');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('name', newPet.name);
      formData.append('type', newPet.species);
      if (newPet.age) formData.append('age', newPet.age);
      if (newPet.breed) formData.append('breed', newPet.breed);
      if (newPet.image) {
        formData.append('image', newPet.image);
      }

      const response = await fetch('http://localhost:8000/pets', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка сервера');
      }

      await fetchPets();

      setShowModal(false);
      setNewPet({ name: '', age: '', species: '', breed: '', image: null });
      setImagePreview('');
      setError('');
    } catch (error) {
      console.error('Ошибка при добавлении питомца:', error);
      setError(error.message || 'Произошла ошибка при сохранении. Пожалуйста, попробуйте снова.');
    }
  };

  return (
    <div className="documentation-container_table_pet">
      <h1>Ваши питомцы</h1>
      <div className="buttons-container">
        <button className="action-button add-button" onClick={handleAddPet}>
          добавить
        </button>
        <button className="action-button delete-button" onClick={handleDeletePet}>
          удалить
        </button>
      </div>

      <div className="pets-table-container">
        {pets && pets.length > 0 ? (
          pets.map(pet => (
            <div key={pet.id} className="pet-card" style={{
              border: '1px solid #ddd',
              borderRadius: '8px',
              padding: '16px',
              margin: '16px 0',
              display: 'flex',
              gap: '20px',
              alignItems: 'center',
              backgroundColor: '#fff'
            }}>
              {/* Блок с изображением */}
              <div style={{
                width: '200px',
                height: '200px',
                flexShrink: 0,
                backgroundColor: '#f5f5f5',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '4px',
                overflow: 'hidden',
                position: 'relative'
              }}>
                {pet.image ? (
                  <img
                    src={`http://localhost:8000${pet.image.startsWith('/') ? '' : '/'}${pet.image}`}
                    alt={`${pet.name}`}
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                      objectPosition: 'center'
                    }}
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/default-pet.jpg';
                    }}
                  />
                ) : (
                  <div style={{
                    color: '#666',
                    fontSize: '16px',
                    textAlign: 'center',
                    padding: '16px'
                  }}>
                    Изображение отсутствует
                  </div>
                )}
              </div>

              {/* Блок с информацией */}
              <div style={{ flexGrow: 1 }}>
                <h3 style={{ 
                  marginTop: 0, 
                  marginBottom: '12px',
                  color: '#333'
                }}>
                  {pet.name}
                </h3>

                <table style={{ 
                  width: '100%', 
                  borderCollapse: 'collapse',
                  fontSize: '15px'
                }}>
                  <tbody>
                    <tr>
                      <td style={{
                        padding: '6px 0',
                        width: '100px',
                        fontWeight: 'bold',
                        verticalAlign: 'top',
                        color: '#555'
                      }}>Вид:</td>
                      <td style={{ 
                        padding: '6px 0',
                        color: '#333'
                      }}>
                        {pet.type === 'кот' ? 'Кошка' : 'Собака'}
                      </td>
                    </tr>

                    {pet.age !== null && pet.age !== undefined && (
                      <tr>
                        <td style={{
                          padding: '6px 0',
                          fontWeight: 'bold',
                          verticalAlign: 'top',
                          color: '#555'
                        }}>Возраст:</td>
                        <td style={{ 
                          padding: '6px 0',
                          color: '#333'
                        }}>
                          {pet.age} {getAgeWord(pet.age)}
                        </td>
                      </tr>
                    )}

                    {pet.breed && (
                      <tr>
                        <td style={{
                          padding: '6px 0',
                          fontWeight: 'bold',
                          verticalAlign: 'top',
                          color: '#555'
                        }}>Порода:</td>
                        <td style={{ 
                          padding: '6px 0',
                          color: '#333'
                        }}>
                          {pet.breed}
                        </td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          ))
        ) : (
          <div className="no-pets-message" style={{
            textAlign: 'center',
            padding: '40px',
            fontSize: '18px',
            color: '#666'
          }}>
            У вас пока нет добавленных питомцев
          </div>
        )}
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Добавить нового питомца</h2>

            <div className="form-group">
              <label>Имя:</label>
              <input
                type="text"
                name="name"
                value={newPet.name}
                onChange={handleInputChange}
                placeholder="Введите имя питомца"
                required
              />
            </div>

            <div className="form-group">
              <label>Возраст:</label>
              <input
                type="number"
                name="age"
                value={newPet.age}
                onChange={handleInputChange}
                placeholder="Введите возраст"
              />
            </div>

            <div className="form-group">
              <label>Вид:</label>
              <input
                type="text"
                name="species"
                value={newPet.species}
                onChange={handleInputChange}
                placeholder="Введите 'кот' или 'собака'"
                required
              />
            </div>

            <div className="form-group">
              <label>Порода:</label>
              <input
                type="text"
                name="breed"
                value={newPet.breed}
                onChange={handleInputChange}
                placeholder="Введите породу"
              />
            </div>

            <div className="form-group">
              <label>Изображение:</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
              />
              {imagePreview && (
                <div className="image-preview">
                  <img src={imagePreview} alt="Предпросмотр" style={{ maxWidth: '100px', maxHeight: '100px', marginTop: '10px' }} />
                </div>
              )}
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="modal-buttons">
              <button 
                className="action-button cancel-button"
                onClick={() => {
                  setShowModal(false);
                  setError('');
                  setImagePreview('');
                }}
              >
                Отмена
              </button>
              <button 
                className="action-button save-button"
                onClick={handleSavePet}
              >
                Сохранить
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

function getAgeWord(age) {
  const lastDigit = age % 10;
  const lastTwoDigits = age % 100;

  if (lastTwoDigits >= 11 && lastTwoDigits <= 19) return 'лет';
  if (lastDigit === 1) return 'год';
  if (lastDigit >= 2 && lastDigit <= 4) return 'года';
  return 'лет';
}

export default PetsPage;
