import axios from 'axios';
import React, { useEffect, useState } from 'react';



const PetsPage = () => {
  const [data, setData] = useState([]);
  const [pets, setPets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newPet, setNewPet] = useState({
    name: '',
    age: '',
    species: '',
    breed: '',
    image: null
  });
  const [error, setError] = useState('');
  const [imagePreview, setImagePreview] = useState('');

  useEffect(() => {
    fetchEvents();
    fetchPets();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:8000/events');
      const events = response.data;

      const groupedData = events.reduce((acc, event) => {
        const { name, event_type_id } = event;

        if (!acc[name]) {
          acc[name] = {
            name,
            events: { 1: 0, 2: 0, 3: 0, 4: 0 }
          };
        }

        acc[name].events[event_type_id] += 1;
        return acc;
      }, {});

      const processedData = Object.values(groupedData).map(group => ({
        name: group.name,
        events: Object.fromEntries(
          Object.entries(group.events).map(([eventTypeId, seconds]) => [
            eventTypeText[eventTypeId], Math.floor(seconds)
          ])
        )
      }));

      setData(processedData);
    } catch (error) {
      console.error('Ошибка при получении данных событий:', error);
    }
  };

  const fetchPets = async () => {
    try {
      const response = await fetch('http://localhost:8000/pets');
      const data = await response.json();
      setPets(data);
    } catch (error) {
      console.error('Ошибка загрузки питомцев:', error);
      setError('Не удалось загрузить список питомцев');
    }
  };

  const eventTypeText = {
    1: 'The animal is actively moving',
    2: 'The animal is at peace or relax',
    3: 'The animal eats',
    4: 'The animal goes to the toilet',
  };

  const handleAddPet = () => {
    setShowModal(true);
  };

  const handleDeletePet = () => {
    console.log('Удаление питомца');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPet(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewPet(prev => ({ ...prev, image: file }));

      // Создаем превью изображения
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSavePet = async () => {
    const lowerSpecies = newPet.species.toLowerCase();
    if (lowerSpecies !== 'кот' && lowerSpecies !== 'собака') {
      setError('Пожалуйста, введите "кот" или "собака" в поле "Вид"');
      return;
    }

    if (!newPet.name) {
      setError('Пожалуйста, введите имя питомца');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('name', newPet.name);
      formData.append('type', lowerSpecies);
      if (newPet.age) formData.append('age', newPet.age);
      if (newPet.breed) formData.append('breed', newPet.breed);
      if (newPet.image) {
        formData.append('image', newPet.image);
      }

      const response = await fetch('http://localhost:8000/pets', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка сервера');
      }

      // Обновляем список питомцев после успешного добавления
      await fetchPets();

      setShowModal(false);
      setNewPet({ name: '', age: '', species: '', breed: '', image: null });
      setImagePreview('');
      setError('');
    } catch (error) {
      console.error('Ошибка при добавлении питомца:', error);
      setError(error.message || 'Произошла ошибка при сохранении. Пожалуйста, попробуйте снова.');
    }
  };


  return (
    <div className="documentation-container_table">
      <h1>Ваши питомцы</h1>
      <div className="buttons-container">
        <button className="action-button add-button" onClick={handleAddPet}>
          добавить
        </button>
        <button className="action-button delete-button" onClick={handleDeletePet}>
          удалить
        </button>
      </div>

        {/* Список добавленных питомцев */}
      <div className="pets-table-container">
        {pets.length > 0 ? (
          pets.map(pet => (
            <div key={pet.id} className="pet-row">
              <div className="pet-image-container">
                {pet.image ? (
                  <img 
                    src={`http://localhost:8000/uploads/${pet.image.split('/').pop()}`}  
                    alt={pet.name}
                    className="pet-image"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/default-pet.jpg';
                    }}
                  />
                ) : (
                  <div className="no-image">Нет фото</div>
              )}
           </div>

              <table className="pet-info-table">
              <tbody>
                  <tr>
                  <th>Имя:</th>
                  <td>{pet.name}</td>
                  </tr>
                  <tr>
                  <th>Тип:</th>
                  <td>{pet.type}</td>
                  </tr>
                  {pet.age && (
                  <tr>
                      <th>Возраст:</th>
                      <td>{pet.age} {getAgeWord(pet.age)}</td>
                  </tr>
                  )}
                  {pet.breed && (
                  <tr>
                      <th>Порода:</th>
                      <td>{pet.breed}</td>
                  </tr>
                  )}
              </tbody>
              </table>
         </div>
       ))
     ) : (
        <p>У вас пока нет добавленных питомцев</p>
     )}
   </div>

      {data.length > 0 ? (
        data.map((group, index) => (
          <div key={index} className="table-container">
            <div className="pet-photo">
              <img
                src={`/${group.name}.jpg`}
                alt={group.name}
                className="pet-photo-img"
                onError={(e) => {
                  e.target.onerror = null; 
                  e.target.src = '/default-pet.jpg';
                }}
              />
            </div>

            <div className="event-data">
              <h2>{group.name}</h2>

              <table className="event-table">
                <thead>
                  <tr>
                    <th>Тип события</th>
                    <th>Сумма времени (минуты)</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(group.events).map(([eventTypeName, totalMinutes]) => (
                    <tr key={eventTypeName}>
                      <td>{eventTypeName}</td>
                      <td>{totalMinutes}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ))
      ) : (
        <p>Данные отсутствуют</p>
      )}

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Добавить нового питомца</h2>

            <div className="form-group">
              <label>Имя:</label>
              <input
                type="text"
                name="name"
                value={newPet.name}
                onChange={handleInputChange}
                placeholder="Введите имя питомца"
                required
              />
            </div>

            <div className="form-group">
              <label>Возраст:</label>
              <input
                type="number"
                name="age"
                value={newPet.age}
                onChange={handleInputChange}
                placeholder="Введите возраст"
              />
            </div>

            <div className="form-group">
              <label>Вид:</label>
              <input
                type="text"
                name="species"
                value={newPet.species}
                onChange={handleInputChange}
                placeholder="Введите 'кот' или 'собака'"
                required
              />
            </div>

            <div className="form-group">
              <label>Порода:</label>
              <input
                type="text"
                name="breed"
                value={newPet.breed}
                onChange={handleInputChange}
                placeholder="Введите породу"
              />
            </div>

            <div className="form-group">
              <label>Изображение:</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
              />
              {imagePreview && (
                <div className="image-preview">
                  <img src={imagePreview} alt="Предпросмотр" style={{ maxWidth: '100px', maxHeight: '100px', marginTop: '10px' }} />
                </div>
              )}
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="modal-buttons">
              <button 
                className="action-button cancel-button"
                onClick={() => {
                  setShowModal(false);
                  setError('');
                  setImagePreview('');
                }}
              >
                Отмена
              </button>
              <button 
                className="action-button save-button"
                onClick={handleSavePet}
              >
                Сохранить
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};


// Вспомогательная функция для склонения слова "год"
function getAgeWord(age) {
    if (age % 100 >= 11 && age % 100 <= 14) {
      return 'лет';
    }
    switch (age % 10) {
      case 1: return 'год';
      case 2:
      case 3:
      case 4: return 'года';
      default: return 'лет';
    }
  }

export default PetsPage;
