import React, { useEffect, useState } from 'react';

// Компонент для отображения таблицы
const EventTable = () => {
  const [eventData, setEventData] = useState([]);

  // Функция для получения данных с сервера
  const fetchEventData = async () => {
    try {
      const response = await fetch('/event-summary'); // URL вашего API
      const data = await response.json();
      setEventData(data);  // Сохраняем полученные данные в состоянии
    } catch (error) {
      console.error('Ошибка при получении данных:', error);
    }
  };

  useEffect(() => {
    fetchEventData();  // Вызываем функцию при монтировании компонента
  }, []); // Пустой массив зависимостей, чтобы запрос выполнялся только один раз

  return (
    <div>
      {eventData.length === 0 ? (
        <p>Загрузка данных...</p> // Пока данные не загружены
      ) : (
        eventData.map((item, index) => (
          <div key={index} style={{ marginBottom: '40px' }}>
            <h3>{item.name}</h3>
            <table border="1" cellPadding="10" style={{ width: '100%', textAlign: 'center' }}>
              <thead>
                <tr>
                  <th>Тип события</th>
                  <th>Сумма времени</th>
                </tr>
              </thead>
              <tbody>
                {item.event_type_summary.map((event, eventIndex) => (
                  <tr key={eventIndex}>
                    <td>{event.event_type_id}</td>
                    <td>{event.time}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))
      )}
    </div>
  );
};

export default EventTable;
