import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function Sidebar() {
    const [activePage, setActivePage] = useState('/'); // Храним активную страницу
    const navigate = useNavigate();
    const location = useLocation(); // Используем для определения текущего маршрута

    const handleNavigation = (page) => {
        setActivePage(page); // Обновляем активную страницу
        navigate(page); // Перенаправляем на нужный маршрут
    };

    return (
        <div className="sidebar">
            <ul>
                <li
                    className={activePage === '/' || location.pathname === '/' ? 'active' : ''}
                    onClick={() => handleNavigation('/')}
                >
                    Домашняя страница
                </li>
                <li
                    className={activePage === '/table' || location.pathname === '/table' ? 'active' : ''}
                    onClick={() => handleNavigation('/table')}
                >
                    Мониторинг активности
                </li>
                <li
                    className={activePage === '/detect' || location.pathname === '/detect' ? 'active' : ''}
                    onClick={() => handleNavigation('/detect')}
                >
                    Наша модель
                </li>
                <li
                    className={activePage === '/graf' || location.pathname === '/graf' ? 'active' : ''}
                    onClick={() => handleNavigation('/graf')}
                >
                    Графики активности
                </li>
                <li
                    className={activePage === '/doc' || location.pathname === '/doc' ? 'active' : ''}
                    onClick={() => handleNavigation('/doc')}
                >
                    Интересные статьи
                </li>
                <li
                    className={activePage === '/team' || location.pathname === '/team' ? 'active' : ''}
                    onClick={() => handleNavigation('/team')}
                >
                    Наша команда
                </li>
            </ul>
        </div>
    );
}

export default Sidebar;
