import axios from 'axios';
import React, { useEffect, useState } from 'react';


const PetsPage = () => {
  const [data, setData] = useState([]);

  // Словарь для отображения типов событий в текстовом виде
  const eventTypeText = {
    1: 'The animal is actively moving',
    2: 'The animal is at peace or relax',
    3: 'The animal eats',
    4: 'The animal goes to the toilet',
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:8000/events');
        const events = response.data;

        // Группируем события по уникальным именам
        const groupedData = events.reduce((acc, event) => {
          const { name, event_type_id } = event;

          // Если еще нет группы для такого имени, создаем пустую
          if (!acc[name]) {
            acc[name] = {
              name,
              events: {
                1: 0,
                2: 0,
                3: 0,
                4: 0
              }
            };
          }

          // Прибавляем 1 секунду для каждого события типа
          acc[name].events[event_type_id] += 1;

          return acc;
        }, {});

        const processedData = Object.values(groupedData).map(group => ({
          name: group.name,
          events: Object.fromEntries(
            Object.entries(group.events).map(([eventTypeId, seconds]) => [
              eventTypeText[eventTypeId], Math.floor(seconds)
            ])
          )
        }));

        setData(processedData);
      } catch (error) {
        console.error('Ошибка при получении данных:', error);
      }
    };

    fetchEvents();
  }, []);

  // Обработчики для кнопок
  const handleAddPet = () => {
    console.log('Добавление нового питомца');
    // Здесь будет логика добавления питомца
  };

  const handleDeletePet = () => {
    console.log('Удаление питомца');
    // Здесь будет логика удаления питомца
  };

  return (
    <div className="documentation-container_table">
      <h1>Ваши питомцы</h1>
      <div className="buttons-container">
        <button 
          className="action-button add-button"
          onClick={handleAddPet}
        >
          добавить
        </button>
        <button 
          className="action-button delete-button"
          onClick={handleDeletePet}
        >
          удалить
        </button>
      </div>

      {data.length > 0 ? (
        data.map((group, index) => (
          <div key={index} className="table-container">
            {/* Изображение питомца */}
            <div className="pet-photo">
              <img
                src={`/${group.name}.jpg`}
                alt={group.name}
                className="pet-photo-img"
                onError={(e) => {
                  e.target.onerror = null; 
                  e.target.src = '/default-pet.jpg';
                }}
              />
            </div>

            {/* Информация о питомце */}
            <div className="event-data">
              <h2>{group.name}</h2>

              {/* Таблица событий */}
              <table className="event-table">
                <thead>
                  <tr>
                    <th>Тип события</th>
                    <th>Сумма времени (минуты)</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(group.events).map(([eventTypeName, totalMinutes]) => (
                    <tr key={eventTypeName}>
                      <td>{eventTypeName}</td>
                      <td>{totalMinutes}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ))
      ) : (
        <p>Данные отсутствуют</p>
      )}
    </div>
  );
};

export default PetsPage;
