import React, { useEffect, useState, useCallback } from 'react';

const Cameras = () => {
  const [cameras, setCameras] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newCamera, setNewCamera] = useState({
    room: '',
    wifi_name: '',
    wifi_pass: '',
    ip_camera: '',
    port_camera: '',
    type_camera: ''
  });
  const [error, setError] = useState('');
  const [lastUpdate, setLastUpdate] = useState(Date.now());
  const [selectedCamera, setSelectedCamera] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);

// Функция загрузки данных с useCallback для стабильной ссылки
  const fetchCameras = useCallback(async (force = false) => {
    try {
      const url = `http://localhost:8000/cameras?_=${force ? Date.now() : lastUpdate}`;
      const response = await fetch(url);

      if (!response.ok) return;

      const result = await response.json();

      if (result.status === 'success') {
        if (JSON.stringify(cameras) !== JSON.stringify(result.data)) {
          setCameras(result.data);
          setLastUpdate(Date.now());
        }
      }
    } catch (error) {
      console.error('Ошибка загрузки камер:', error);
      setError('Ошибка загрузки данных');
    }
  }, [lastUpdate, cameras]);


 // Автоматическое обновление
  useEffect(() => {
    let isMounted = true;

    const checkUpdates = async () => {
      if (!isMounted) return;
      await fetchCameras();
      setTimeout(checkUpdates, 3000);
    };

    checkUpdates();

    return () => { isMounted = false; };
  }, [fetchCameras]); // Добавлена зависимость fetchCameras


    // Принудительное обновление при любых действиях
  const handleAction = async (action) => {
    try {
      await action();
      await fetchCameras(true); // Принудительное обновление после действия
      forceRefresh();
    } catch (err) {
      console.error(err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCamera(prev => ({ ...prev, [name]: value }));
    if (error) setError('');
  };

  const handleAddCamera = () => {
    setShowModal(true);
    setError('');
  };

  const handleDeleteCamera = () => {
    if (cameras.length === 0) {
      setError('Нет камер для удаления');
      return;
    }
    setShowDeleteModal(true);
  };

  const confirmDeleteCamera = async () => {
    await handleAction(async () => {
      if (!selectedCamera) return;

      const response = await fetch(`http://localhost:8000/cameras/${selectedCamera.id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка при удалении камеры');
      }

      setShowDeleteModal(false);
      setSelectedCamera(null);
    });
  };

  // Обработчики с интегрированным обновлением
  const handleSaveCamera = async () => {
    await handleAction(async () => {
      if (!newCamera.room || !newCamera.ip_camera) {
        throw new Error('Название комнаты и IP камеры обязательны');
      }

      setIsLoading(true);
      const response = await fetch('http://localhost:8000/cameras', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newCamera),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка сервера');
      }

      setShowModal(false);
      setNewCamera({
        room: '', wifi_name: '', wifi_pass: '',
        ip_camera: '', port_camera: '', type_camera: ''
      });
    });
  };

  return (
    <div className="documentation-container_table_pet">
      <h1>Список камер</h1>
      <div className="buttons-container">
        <button className="action-button add-button" onClick={handleAddCamera}>
          добавить
        </button>
        <button className="action-button delete-button" onClick={handleDeleteCamera}>
          удалить
        </button>
      </div>

      <div className="cameras-list-container">
        {cameras.length > 0 ? (
          cameras.map(camera => (
            <div key={camera.id} className="camera-item">
              <div className="camera-room">{camera.room}</div>
            </div>
          ))
        ) : (
          <div className="no-cameras-message">
            Нет добавленных камер
          </div>
        )}
      </div>

      {showModal && (
        <div className="modal-overlay">
         <div className="modal-content">
            <h2>Добавить камеру</h2>

            <div className="form-group">
              <label>Комната:</label>
              <input
                  type="text"
                  name="room"
                  value={newCamera.room}
                  onChange={handleInputChange}
                  placeholder="Например: Гостиная"
                  required
              />
            </div>

            <div className="form-group">
              <label>Wi-Fi сеть:</label>
              <input
                  type="text"
                  name="wifi_name"
                  value={newCamera.wifi_name}
                  onChange={handleInputChange}
                  placeholder="Имя Wi-Fi сети"
              />
            </div>

            <div className="form-group">
              <label>Пароль Wi-Fi:</label>
              <input
                  type="password"
                  name="wifi_pass"
                  value={newCamera.wifi_pass}
                  onChange={handleInputChange}
                  placeholder="Пароль от Wi-Fi"
              />
            </div>

            <div className="form-group">
              <label>IP камеры:</label>
              <input
                  type="text"
                  name="ip_camera"
                  value={newCamera.ip_camera}
                  onChange={handleInputChange}
                  placeholder="Например: 192.168.1.10"
                  required
              />
            </div>

            <div className="form-group">
              <label>Порт камеры:</label>
              <input
                  type="number"
                  name="port_camera"
                  value={newCamera.port_camera}
                  onChange={handleInputChange}
                  placeholder="Например: 8080"
              />
            </div>

            <div className="form-group">
              <label>Тип камеры:</label>
              <input
                  type="text"
                  name="type_camera"
                  value={newCamera.type_camera}
                  onChange={handleInputChange}
                  placeholder="Например: IP-камера"
              />
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="modal-buttons">
              <button 
                  className="action-button cancel-button"
                  onClick={() => {
                  setShowModal(false);
                  setError('');
                  }}
              >
                  Отмена
              </button>
              <button 
                  className="action-button save-button"
                  onClick={handleSaveCamera}
              >
                  Сохранить
              </button>
            </div>
          </div>
        </div>
    )}

      {/* Модальное окно для удаления */}
      {showDeleteModal && (
        <div className="modal-overlay">
            <div className="modal-content">
            <h2>Подтверждение удаления</h2>
            <p>Вы уверены, что хотите удалить камеру из комнаты "{selectedCamera?.room}"?</p>
            <div className="modal-buttons">
                <button 
                className="action-button cancel-button"
                onClick={() => setShowDeleteModal(false)}
                >
                Отмена
                </button>
                <button 
                className="action-button delete-button"
                onClick={confirmDeleteCamera}
                >
                Удалить
                </button>
            </div>
            </div>
        </div>
        )}

    </div>
  );
};

export default Cameras;
