import React, { useState } from 'react';
import axios from 'axios';


const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [text, setText] = useState([
    "the animal eats",
    "the animal is actively moving",
    "the animal is at peace or relax",
    "the animal goes to the toilet"
  ]);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [fileName, setFileName] = useState('');

  const handleImageChange = (e) => {
    const file = e.target.files[0];

    const allowedTypes = ['image/jpeg', 'image/png', 'image/jpg'];
    if (file && allowedTypes.includes(file.type)) {
      setError(null);
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
      setFileName(file.name);
    } else {
      setError('Invalid file type. Please upload a JPEG, JPG, or PNG image.');
      setImage(null);
      setImagePreview(null);
      setFileName('');
    }
  };

  const handleSubmit = async () => {
    if (!image) {
      setError('Please upload a valid image first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', image);
    formData.append('text_input', JSON.stringify({ text }));

    try {
      const response = await axios.post('http://localhost:8000/predict/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setResult(response.data);
    } catch (error) {
      console.error("Error sending request:", error.response || error.message);
    }
  };

  return (
    <div className="app-container">
      <div className="main-content">
        {/* Левая колонка с загрузкой и кнопкой Submit */}
        <div className="upload-container">
          <input
            type="file"
            id="file-input"
            onChange={handleImageChange}
            accept="image/jpeg, image/png, image/jpg" // Ограничение на типы файлов
          />
          <label htmlFor="file-input">Choose a File</label>
          <span className="file-name">{fileName ? fileName : 'No file chosen'}</span>
          <button onClick={handleSubmit}>Submit</button>
        </div>

        {/* Ошибка при неправильном формате изображения */}
        {error && <div className="error-message">{error}</div>}

        {/* Результаты и изображение рядом */}
        {imagePreview && result && (
          <div className="result-container">
            {/* Блок с изображением */}
            <div className="image-preview">
              <h2>Uploaded Image:</h2>
              <img src={imagePreview} alt="Uploaded Preview" />
            </div>

            {/* Блок с результатами предсказания */}
            <div className="prediction">
              <h2>Prediction Result:</h2>
              <p><strong>Predicted Text:</strong> {result.predicted_text}</p>
              <h3>Probabilities:</h3>
              <ul>
                {result.probabilities.map((prob, index) => (
                  <li key={index}>{text[index]}: {prob}</li>
                ))}
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
