import React, { useState } from 'react';
import axios from 'axios';
import './header.css';

function Header() {
  const [isRegisterOpen, setIsRegisterOpen] = useState(false);
  const [isLoginOpen, setIsLoginOpen] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Открыть модальное окно для регистрации
  const openRegisterModal = () => setIsRegisterOpen(true);
  const closeRegisterModal = () => setIsRegisterOpen(false);

  // Открыть модальное окно для входа
  const openLoginModal = () => setIsLoginOpen(true);
  const closeLoginModal = () => setIsLoginOpen(false);

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:8000/register', {
        username,
        password,
      });
      alert('User registered successfully');
      closeRegisterModal();
    } catch (error) {
      console.error(error);
      alert('Error during registration');
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8000/login', {
        username,
        password,
      });
      alert('Login successful');
      closeLoginModal();
    } catch (error) {
      console.error(error);
      alert('Invalid credentials');
    }
  };

  return (
    <header className="header">
      <div className="left-side">
        <input className="search-bar" type="text" placeholder="Search" />
        <button className="search-button">
          <div className="tooltip">Search</div>
        </button>
      </div>

      <div className="right-side">
        <button className='register-button' onClick={openRegisterModal}>Зарегистрироваться</button>
        <button className='login-button' onClick={openLoginModal}>Войти</button>
      </div>

      {/* Модальное окно для регистрации */}
      {isRegisterOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeRegisterModal}>&times;</span>
            <h2>Регистрация</h2>
            <input
              type="text"
              placeholder="Имя пользователя"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleRegister}>Зарегистрироваться</button>
          </div>
        </div>
      )}

      {/* Модальное окно для входа */}
      {isLoginOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeLoginModal}>&times;</span>
            <h2>Вход</h2>
            <input
              type="text"
              placeholder="Имя пользователя"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Войти</button>
          </div>
        </div>
      )}
    </header>
  );
}

export default Header;
