import React, { useEffect } from 'react';
import { useSearch } from './SearchContext';

function AboutUs() {
  const { query } = useSearch();

  useEffect(() => {
    const highlightText = () => {
      const highlightedElements = document.querySelectorAll('.highlight');
      highlightedElements.forEach((element) => {
        element.classList.remove('highlight');
      });

      if (query) {
        const regExp = new RegExp(query, 'gi');
        const elements = document.querySelectorAll('.aboutUs *');

        elements.forEach((element) => {
          if (element.children.length === 0 && element.textContent.match(regExp)) {
            const newHtml = element.innerHTML.replace(
              regExp,
              (match) => `<span class="highlight">${match}</span>`
            );
            element.innerHTML = newHtml;
          }
        });
      }
    };

    highlightText();
  }, [query]);

  return (
    <div className="aboutUs">
      <h2>Список статей</h2>
      <p>
        Список статей))... 
        Здесь можно найти информацию о питомцах, их поведении и особенностях ухода за ними.
      </p>
    </div>
  );
}

export default AboutUs;
