import React, { useEffect, useState } from 'react';

const Cameras = () => {
  const [cameras, setCameras] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newCamera, setNewCamera] = useState({
    room: '',
    wifi_name: '',
    wifi_pass: '',
    ip_camera: '',
    port_camera: '',
    type_camera: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchCameras();
  }, []);

  const fetchCameras = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/cameras');

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();

      if (result.status === 'success') {
        setCameras(result.data);
      } else {
        throw new Error(result.message || 'Неизвестная ошибка сервера');
      }
    } catch (error) {
      console.error('Ошибка загрузки камер:', error);
      setError('Не удалось загрузить список камер: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCamera(prev => ({ ...prev, [name]: value }));
    if (error) setError('');
  };

  const handleAddCamera = () => {
    setShowModal(true);
    setError('');
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Проверка типа файла
      if (!file.type.match('image.*')) {
        setError('Пожалуйста, загрузите изображение');
        return;
      }

      // Проверка размера файла (макс 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setError('Размер изображения не должен превышать 5MB');
        return;
      }

      setNewPet(prev => ({ ...prev, image: file }));

      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };


  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedPet, setSelectedPet] = useState(null);

  const handleDeletePet = () => {
    if (pets.length === 0) {
      setError('Нет питомцев для удаления');
      return;
    }
    setShowDeleteModal(true);
  };

  const confirmDeletePet = async () => {
    if (!selectedPet) return;

    try {
      const response = await fetch(`http://localhost:8000/pets/by_name/${encodeURIComponent(selectedPet)}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка при удалении питомца');
      }

      await fetchPets();
      setError('');
      setShowDeleteModal(false);
      setSelectedPet(null);

    } catch (error) {
      console.error('Ошибка при удалении питомца:', error);
      setError(error.message);
    }
  };

  const handleSaveCamera = async () => {
    try {
      // Валидация обязательных полей
      if (!newCamera.room || !newCamera.ip_camera) {
        throw new Error('Название комнаты и IP камеры обязательны');
      }

      const response = await fetch('http://localhost:8000/cameras', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newCamera),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ошибка сервера');
      }

      const result = await response.json();
      if (result.status === 'success' && result.data) {
        setCameras(prev => [...prev, result.data]);
      }

      setShowModal(false);
      setNewCamera({
        room: '',
        wifi_name: '',
        wifi_pass: '',
        ip_camera: '',
        port_camera: '',
        type_camera: ''
      });
      setError('');
    } catch (error) {
      console.error('Ошибка:', error);
      setError(error.message);
    }
  };


// Функция для склонения слова "год"
  const getAgeWord = (age) => {
    if (age % 100 >= 11 && age % 100 <= 19) return 'лет';

    switch(age % 10) {
      case 1: return 'год';
      case 2:
      case 3:
      case 4: return 'года';
      default: return 'лет';
    }
  };

  const getImageUrl = (pet) => {
    if (!pet?.image) return null;

    try {
      let imagePath = pet.image;

      // Если путь не начинается с /uploads, добавляем его
      if (!imagePath.startsWith('/uploads') && !imagePath.startsWith('http')) {
        imagePath = `/uploads/${imagePath}`;
      }

      // Декодируем URL один раз
      const decodedPath = decodeURIComponent(imagePath);

      // Для локального development
      if (!decodedPath.startsWith('http')) {
        return `http://localhost:8000${decodedPath}?t=${Date.now()}`;
      }

      return `${decodedPath}?t=${Date.now()}`;
    } catch (e) {
      console.error('Ошибка обработки URL:', e);
      return null;
    }
  };

  const calculatePetAge = (year) => {
    if (!year) return null; // Если год не указан

    const currentYear = new Date().getFullYear();
    const age = currentYear - parseInt(year);

    // Если год совпадает с текущим - возвращаем 1
    if (age === 0) return 1;

    // Для прошлых лет возвращаем возраст, для будущих - null
    return age > 0 ? age : null;
  };



  return (
    <div className="documentation-container_table_pet">
      <h1>Список камер</h1>
      <div className="buttons-container">
        <button className="action-button add-button" onClick={handleAddPet}>
          добавить
        </button>
        <button className="action-button delete-button" onClick={handleDeletePet}>
          удалить
        </button>
      </div>

      <div className="cameras-table-container">
        {cameras.length > 0 ? (
            <table className="cameras-table">
            <thead>
                <tr>
                <th>Комната</th>
                </tr>
            </thead>
            <tbody>
                {cameras.map(camera => (
                <tr key={camera.id}>
                    <td>{camera.room}</td>
                </tr>
                ))}
            </tbody>
            </table>
        ) : (
            <div className="no-cameras-message">
            Нет добавленных камер
            </div>
        )}
        </div>

      {showModal && (
        <div className="modal-overlay">
        <div className="modal-content">
            <h2>Добавить камеру</h2>

            <div className="form-group">
            <label>Комната*:</label>
            <input
                type="text"
                name="room"
                value={newCamera.room}
                onChange={handleInputChange}
                placeholder="Например: Гостиная"
                required
            />
            </div>

            <div className="form-group">
            <label>Wi-Fi сеть:</label>
            <input
                type="text"
                name="wifi_name"
                value={newCamera.wifi_name}
                onChange={handleInputChange}
                placeholder="Имя Wi-Fi сети"
            />
            </div>

            <div className="form-group">
            <label>Пароль Wi-Fi:</label>
            <input
                type="password"
                name="wifi_pass"
                value={newCamera.wifi_pass}
                onChange={handleInputChange}
                placeholder="Пароль от Wi-Fi"
            />
            </div>

            <div className="form-group">
            <label>IP камеры*:</label>
            <input
                type="text"
                name="ip_camera"
                value={newCamera.ip_camera}
                onChange={handleInputChange}
                placeholder="Например: 192.168.1.10"
                required
            />
            </div>

            <div className="form-group">
            <label>Порт камеры:</label>
            <input
                type="number"
                name="port_camera"
                value={newCamera.port_camera}
                onChange={handleInputChange}
                placeholder="Например: 8080"
            />
            </div>

            <div className="form-group">
            <label>Тип камеры:</label>
            <input
                type="text"
                name="type_camera"
                value={newCamera.type_camera}
                onChange={handleInputChange}
                placeholder="Например: IP-камера"
            />
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="modal-buttons">
            <button 
                className="action-button cancel-button"
                onClick={() => {
                setShowModal(false);
                setError('');
                }}
            >
                Отмена
            </button>
            <button 
                className="action-button save-button"
                onClick={handleSaveCamera}
            >
                Сохранить
            </button>
            </div>
        </div>
        </div>
    )}

      {/* Модальное окно для удаления */}
      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Выберите питомца для удаления</h2>

            <div className="pet-list-to-delete">
              {pets.map(pet => (
                <div 
                  key={pet.id}
                  className={`pet-to-delete ${selectedPet === pet.name ? 'selected' : ''}`}
                  onClick={() => setSelectedPet(pet.name)}
                >
                  {pet.name} ({pet.type}, {pet.year})
                </div>
              ))}
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="modal-buttons">
              <button 
                className="action-button cancel-button"
                onClick={() => {
                  setShowDeleteModal(false);
                  setSelectedPet(null);
                  setError('');
                }}
              >
                Отмена
              </button>
              <button 
                className="action-button delete-button"
                onClick={confirmDeletePet}
                disabled={!selectedPet}
              >
                Удалить
              </button>
            </div>
          </div>
        </div>
      )}

    </div>
  );
};

export default Cameras;
