import axios from 'axios';
import React, { useEffect, useState } from 'react';

// Глобальная переменная для хранения ID камеры и лога
window.appGlobals = {
  selectedCameraId: null,
  activityLogId: null
};

const EventsPage = () => {
  const [eventsData, setEventsData] = useState([]);
  const [pets, setPets] = useState([]);
  const [resultsData, setResultsData] = useState([]);
  const [restActivityData, setRestActivityData] = useState([]);
  const [showTrackingOptions, setShowTrackingOptions] = useState(false);
  const [showResultsOptions, setShowResultsOptions] = useState(false);
  const [trackingTime, setTrackingTime] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [cameras, setCameras] = useState([]);
  const [showCameraModal, setShowCameraModal] = useState(false);
  const [selectedCameraId, setSelectedCameraId] = useState(null);
  const [pendingDuration, setPendingDuration] = useState(null);
  const [isMonitoringActive, setIsMonitoringActive] = useState(false);
  const [showResults, setShowResults] = useState(false);

  const eventTypeText = {
    2: 'the animal is actively',
    3: 'the animal is resting',
    1: 'the animal eats',
    4: 'the animal goes to the toilet',
  };

  const getImageUrl = (pet) => {
    if (!pet?.image) return null;

    try {
      let imagePath = pet.image;

      if (!imagePath.startsWith('/uploads') && !imagePath.startsWith('http')) {
        imagePath = `/uploads/${imagePath}`;
      }

      const decodedPath = decodeURIComponent(imagePath);

      if (!decodedPath.startsWith('http')) {
        return `http://localhost:8000${decodedPath}?t=${Date.now()}`;
      }

      return `${decodedPath}?t=${Date.now()}`;
    } catch (e) {
      console.error('Ошибка обработки URL:', e);
      return null;
    }
  };

  useEffect(() => {
    const fetchPets = async () => {
      try {
        const response = await axios.get('http://localhost:8000/pets');

        // Используем тот же формат, что и в PetsPage
        if (response.data.status === 'success') {
          setPets(response.data.data);
        } else {
          setPets(response.data); // Если нет структуры status/data
        }
      } catch (error) {
        console.error('Ошибка при загрузке питомцев:', error);
      }
    };

    const fetchResults = async () => {
      try {
        const response = await axios.get('http://localhost:8000/results_eat_and_tuolet');
        console.log('Raw results data:', response.data);

        // Добавим преобразование данных для единообразия
        const formattedData = response.data.map(item => ({
          ...item,
          pet_id: item.pet_id || item.pets_id, // унифицируем ключ
          count_rank: item.count_rank || item['count(rank)'] // обработка разных имен полей
        }));

        console.log('Formatted results:', formattedData);
        setResultsData(formattedData);
      } catch (error) {
        console.error('Ошибка при загрузке результатов:', error);
        setResultsData([]); // Устанавливаем пустой массив при ошибке
      }
    };

    const fetchRestActivity = async () => {
      try {
        const response = await axios.get('http://localhost:8000/results_rest_and_activity');
        console.log('Rest and activity data:', response.data);
        setRestActivityData(response.data);
      } catch (error) {
        console.error('Ошибка при загрузке данных об активности и отдыхе:', error);
      }
    };

    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:8000/events');
        const events = response.data;

        const groupedData = events.reduce((acc, event) => {
          const { pets_id, events_id } = event;

          if (!acc[pets_id]) {
            acc[pets_id] = {
              pets_id,
              events: {
                1: 0,
                2: 0,
                3: 0,
                4: 0
              }
            };
          }

          acc[pets_id].events[events_id] += 1;
          return acc;
        }, {});

        setEventsData(Object.values(groupedData));
      } catch (error) {
        console.error('Ошибка при загрузке событий:', error);
      }
    };

    const fetchCameras = async () => {
      try {
        const response = await axios.get('http://localhost:8000/cameras');
        setCameras(response.data.data || []);
      } catch (error) {
        console.error('Ошибка при загрузке камер:', error);
      }
    };

    fetchPets();
    fetchRestActivity();
    fetchResults();
    fetchEvents();
    fetchCameras();
  }, []);

  useEffect(() => {
    const savedTracking = localStorage.getItem('petTracking');
    if (savedTracking) {
      const { endTime: savedEndTime, duration } = JSON.parse(savedTracking);
      const endTimeDate = new Date(savedEndTime);
      const now = new Date();

      if (endTimeDate > now) {
        const secondsLeft = Math.floor((endTimeDate - now) / 1000);
        setEndTime(endTimeDate);
        setTimeLeft(secondsLeft);
        setTrackingTime(duration);
        setIsMonitoringActive(true);
      } else {
        localStorage.removeItem('petTracking');
      }
    }
  }, []);

  useEffect(() => {
    if (!endTime) return;

    const timer = setInterval(async() => {
      const now = new Date();
      const secondsLeft = Math.floor((endTime - now) / 1000);

      if (secondsLeft <= 0) {
        clearInterval(timer);
        setTimeLeft(null);
        setTrackingTime(null);
        setEndTime(null);
        setIsMonitoringActive(false);
        localStorage.removeItem('petTracking');
        stopMonitoring();
        // Добавляем остановку мониторинга на бэкенде
        try {
          await stopMonitoring(); // Вызываем функцию остановки
        } catch (error) {
          console.error('Ошибка при автоматической остановке мониторинга:', error);
        }
      } else {
        setTimeLeft(secondsLeft);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [endTime]);

  useEffect(() => {
    if (selectedCameraId !== null) {
      window.appGlobals.selectedCameraId = selectedCameraId;
    }
  }, [selectedCameraId]);

  const logActivity = async () => {
    const now = new Date();
    const recordDate = now.toISOString().slice(0, 19).replace('T', ' ');

    try {
      const response = await axios.post('http://localhost:8000/activity_log');

      if (response.data && response.data.log_id) {
        window.appGlobals.activityLogId = response.data.log_id;
      }

      return response.data.log_id;
    } catch (error) {
      console.error('Ошибка при записи в лог:', error);
      throw error;
    }
  };

  const stopMonitoring = async () => {
    try {
      const response = await axios.post('http://localhost:8000/stop_rtsp_monitoring');
      console.log('Мониторинг остановлен:', response.data);

      // Сбрасываем состояние мониторинга
      setTimeLeft(null);
      setTrackingTime(null);
      setEndTime(null);
      setIsMonitoringActive(false);
      localStorage.removeItem('petTracking');

      return response.data;
    } catch (error) {
      console.error('Ошибка при остановке мониторинга:', error);
      throw error;
    }
  };

  const startTracking = async (duration, seconds) => {
    const endTime = new Date();
    endTime.setSeconds(endTime.getSeconds() + seconds);

    setEndTime(endTime);
    setTimeLeft(seconds);
    setTrackingTime(duration);
    setShowTrackingOptions(false);
    setIsMonitoringActive(true);

    localStorage.setItem('petTracking', JSON.stringify({
      endTime: endTime.toISOString(),
      duration
    }));

    try {
      await logActivity();

      const formData = new FormData();
      formData.append('camera_id', window.appGlobals.selectedCameraId);
      formData.append('activity_id', window.appGlobals.activityLogId);

      const response = await axios.post(
        'http://localhost:8000/start_rtsp_monitoring',
        formData
      );

      console.log('Мониторинг запущен:', response.data);

    } catch (error) {
      console.error('Ошибка при запуске мониторинга:', error);
    }
  };

  const handleStartTracking = (duration) => {
    setPendingDuration(duration);
    setShowCameraModal(true);
    setShowTrackingOptions(false);
  };

  const confirmCameraSelection = () => {
    if (!selectedCameraId) {
      alert('Пожалуйста, выберите камеру');
      return;
    }

    window.appGlobals.selectedCameraId = selectedCameraId;

    console.log('Перед стартом отслеживания:', {
      localState: selectedCameraId,
      globalVar: window.appGlobals.selectedCameraId
    });

    switch(pendingDuration) {
      case '2 часа': 
        startTracking(pendingDuration, 2 * 60 * 60);
        break;
      case '10 часов':
        startTracking(pendingDuration, 10 * 60 * 60);
        break;
      case '1 день':
        startTracking(pendingDuration, 24 * 60 * 60);
        break;
      default:
        return;
    }

    setShowCameraModal(false);
    setPendingDuration(null);
  };

  const handleViewResults = (period) => {
    console.log(`Просмотр результатов за ${period}`);
    setShowResultsOptions(false);

    // Показываем результаты только если выбран "текущие результаты"
    if (period === 'текущие результаты'|| period === '1 день') {
      setShowResults(true);
      setResultsPeriod(period);
    } else {
      setShowResults(false);
      // Здесь можно добавить логику для загрузки данных за другие периоды
    }
  };

  const [resultsPeriod, setResultsPeriod] = useState('текущие результаты');

  const toggleTrackingOptions = () => {
    setShowTrackingOptions(!showTrackingOptions);
    setShowResultsOptions(false);
  };

  const toggleResultsOptions = () => {
    setShowResultsOptions(!showResultsOptions);
    setShowTrackingOptions(false);
  };

  const formatTime = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    return `${hours.toString().padStart(2, '0')} ч ${minutes.toString().padStart(2, '0')} мин ${secs.toString().padStart(2, '0')} сек`;
  };

  const getDailyData = (petId) => {
    const currentDate = new Date().toISOString().split('T')[0]; // Получаем текущую дату в формате YYYY-MM-DD

    // Фильтруем данные по дате и pet_id
    const dailyEatToilet = resultsData.filter(item => 
      (item.pet_id == petId || item.pets_id == petId) && 
      item.date_monitoring?.startsWith(currentDate)
    );

    const dailyRestActivity = restActivityData.filter(item => 
      (item.pet_id == petId || item.pets_id == petId) && 
      item.date_monitoring?.startsWith(currentDate)
    );

    // Суммируем показатели
    const sumEatToilet = dailyEatToilet.reduce((acc, item) => {
      if (item.events_id == 1) acc.eatCount += item.count_rank || item['count(rank)'] || 0;
      if (item.events_id == 4) acc.toiletCount += item.count_rank || item['count(rank)'] || 0;
      return acc;
    }, { eatCount: 0, toiletCount: 0 });

    const sumRestActivity = dailyRestActivity.reduce((acc, item) => {
      if (item.events_id == 2) acc.activityMinutes += item.minutes || 0;
      if (item.events_id == 3) acc.restMinutes += item.minutes || 0;
      return acc;
    }, { activityMinutes: 0, restMinutes: 0 });

    return {
      eatCount: sumEatToilet.eatCount || 0,
      toiletCount: sumEatToilet.toiletCount || 0,
      activityMinutes: sumRestActivity.activityMinutes || 0,
      restMinutes: sumRestActivity.restMinutes || 0
    };
  };


  const renderResultsTable = (petId) => {
    // Функция для форматирования минут в часы и минуты
    const formatMinutes = (minutes) => {
      if (minutes === null || minutes === undefined) return 'Н/Д';
      if (minutes < 60) return `${Math.round(minutes)} мин`;
      const hours = Math.floor(minutes / 60);
      const remainingMinutes = Math.round(minutes % 60);
      return `${hours} ч ${remainingMinutes} мин`;
    };

    // Находим информацию о питомце
    const pet = pets.find(p => p.id === petId) || {};
    const petAge = pet.year ? new Date().getFullYear() - parseInt(pet.year) : null;
    const isCat = pet.animal_type?.toLowerCase() === 'кот';
    const isDog = pet.animal_type?.toLowerCase() === 'собака';
    const currentDate = new Date().toISOString().split('T')[0]; // Текущая дата в формате YYYY-MM-DD

    // Получаем данные для питомца из обеих таблиц
    const petEatToiletResults = resultsData.filter(item => 
      (item.pet_id == petId || item.pets_id == petId)
    );

    const petRestActivityResults = restActivityData.filter(item => 
      (item.pet_id == petId || item.pets_id == petId)
    );

    // Функция для получения данных в зависимости от периода
    const getDisplayData = () => {
      if (resultsPeriod === '1 день') {
        // Данные за день - суммируем все записи за текущую дату
        const dailyEatToilet = petEatToiletResults.filter(item => 
          item.date_monitoring?.startsWith(currentDate)
        );

        const dailyRestActivity = petRestActivityResults.filter(item => 
          item.date_monitoring?.startsWith(currentDate)
        );

        // Суммируем показатели за день
        const sumEatToilet = dailyEatToilet.reduce((acc, item) => {
          const count = item.count_rank || item['count(rank)'] || 0;
          if (item.events_id == 1) acc.eatCount += count;
          if (item.events_id == 4) acc.toiletCount += count;
          return acc;
        }, { eatCount: 0, toiletCount: 0 });

        const sumRestActivity = dailyRestActivity.reduce((acc, item) => {
          const minutes = item.minutes || 0;
          if (item.events_id == 2) acc.activityMinutes += minutes;
          if (item.events_id == 3) acc.restMinutes += minutes;
          return acc;
        }, { activityMinutes: 0, restMinutes: 0 });

        return {
          eatCount: sumEatToilet.eatCount || 0,
          toiletCount: sumEatToilet.toiletCount || 0,
          activityMinutes: sumRestActivity.activityMinutes || 0,
          restMinutes: sumRestActivity.restMinutes || 0,
          isDaily: true
        };
      } else {
        // Данные последней активности - берем только последние записи
        const lastEatToiletActivityId = petEatToiletResults.length > 0 ? 
          Math.max(...petEatToiletResults.map(item => item.activity_id)) : null;

        const lastRestActivityId = petRestActivityResults.length > 0 ? 
          Math.max(...petRestActivityResults.map(item => item.activity_id)) : null;

        const lastEatToilet = lastEatToiletActivityId ? 
          petEatToiletResults.filter(item => item.activity_id === lastEatToiletActivityId) : [];

        const lastRestActivity = lastRestActivityId ? 
          petRestActivityResults.filter(item => item.activity_id === lastRestActivityId) : [];

        const getLastValue = (results, eventId, field) => {
          const result = results.find(item => item.events_id == eventId);
          return result ? (result[field] || result[field.replace('_', ' ')]) : 0;
        };

        return {
          eatCount: getLastValue(lastEatToilet, 1, 'count_rank'),
          toiletCount: getLastValue(lastEatToilet, 4, 'count_rank'),
          activityMinutes: getLastValue(lastRestActivity, 2, 'minutes'),
          restMinutes: getLastValue(lastRestActivity, 3, 'minutes'),
          isDaily: false
        };
      }
    };

    const displayData = getDisplayData();

    // Функция для получения суточных норм
    const getDailyNorms = () => {
      if (!petAge || (!isCat && !isDog)) return {
        eating: 'Н/Д',
        toilet: 'Н/Д',
        activity: 'Н/Д',
        rest: 'Н/Д'
      };

      // Для котят/щенков (до 1 года)
      if (petAge <= 1) {
        return isCat ? {
          eating: '3-4 раза',
          toilet: '6-8 раз',
          activity: '3-5 часов',
          rest: '19-21 час'
        } : {
          eating: '3-4 раза',
          toilet: '8-10 раз',
          activity: '4-6 часов',
          rest: '18-20 часов'
        };
      }
      // Для взрослых (1-7 лет)
      else if (petAge > 1 && petAge < 7) {
        return isCat ? {
          eating: '2-3 раза',
          toilet: '2-4 раза',
          activity: '10 часов',
          rest: '14 часов'
        } : {
          eating: '2 раза',
          toilet: '3-5 раз',
          activity: '12 часов',
          rest: '12 часов'
        };
      }
      // Для пожилых (7+ лет)
      else {
        return isCat ? {
          eating: '2-3 раза',
          toilet: '3-5 раз',
          activity: '4 часа',
          rest: '20 часов'
        } : {
          eating: '2 раза',
          toilet: '4-6 раз',
          activity: '8 часов',
          rest: '16 часов'
        };
      }
    };

    const dailyNorms = getDailyNorms();

    return (
      <table className="event-table">
        <thead>
          <tr>
            <th>Показатели</th>
            <th>Собранные данные ({displayData.isDaily ? 'за день' : 'последняя активность'})</th>
            <th>Средняя норма в сутки</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Количество кормления</td>
            <td>{displayData.eatCount || 'Н/Д'}</td>
            <td>{dailyNorms.eating}</td>
          </tr>
          <tr>
            <td>Количество походов в туалет</td>
            <td>{displayData.toiletCount || 'Н/Д'}</td>
            <td>{dailyNorms.toilet}</td>
          </tr>
          <tr>
            <td>Время активности</td>
            <td>{formatMinutes(displayData.activityMinutes)}</td>
            <td>{dailyNorms.activity}</td>
          </tr>
          <tr>
            <td>Время сна</td>
            <td>{formatMinutes(displayData.restMinutes)}</td>
            <td>{dailyNorms.rest}</td>
          </tr>
        </tbody>
      </table>
    );
  };


  return (
    <div className="documentation-container_table">
      <h1>Активность ваших питомцев</h1>

      <div className="buttons-container_active">
        <div className="dropdown">
          <button className="track-button" onClick={toggleTrackingOptions}>
            Запустить отслеживание
          </button>
          {showTrackingOptions && (
            <div className="dropdown-content">
              <div onClick={() => handleStartTracking('2 часа')}>2 часа</div>
              <div onClick={() => handleStartTracking('10 часов')}>10 часов</div>
              <div onClick={() => handleStartTracking('1 день')}>1 день</div>
            </div>
          )}
        </div>

        <div className="dropdown">
            <button className="results-button" onClick={toggleResultsOptions}>
              Посмотреть результаты
            </button>
            {showResultsOptions && (
              <div className="dropdown-content">
                <div onClick={() => handleViewResults('текущие результаты')}>Текущие результаты</div>
                <div onClick={() => handleViewResults('1 день')}>1 день</div>
                <div onClick={() => handleViewResults('1 неделя')}>1 неделя</div>
              </div>
            )}
        </div>

        {isMonitoringActive && (
          <button 
            className="stop-monitoring-button"
            onClick={stopMonitoring}
          >
            Прервать мониторинг
          </button>
        )}
      </div>

      {timeLeft !== null && (
        <div className="tracking-info">
          <p>До конца отслеживания осталось: {formatTime(timeLeft)}</p>
          {selectedCameraId && (
            <p>
              Выбранная камера: {cameras.find(c => c.id === selectedCameraId)?.room || 'неизвестно'} 
            </p>
          )}
        </div>
      )}

      {showResults && eventsData.length > 0 ? (
        <div className="pets-table-container">
          {eventsData.map(group => {
            const pet = pets.find(p => p.id === group.pets_id) || {};

            return (
              <div key={group.pets_id} className="pet-row">
                <h2 className="pet-name-header">{pet.name || `Питомец #${group.pets_id}`}</h2>
                <div className="pet-content-wrapper"> 
                  <div className="pet-image-container">
                    <img 
                      style={{ maxWidth: '400px', maxHeight: '300px' }}
                      src={getImageUrl(pet) || '/default-pet.jpg'}
                      alt={pet.name || 'Питомец'}
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/default-pet.jpg';
                      }}
                    />
                  </div>

                  {renderResultsTable(group.pets_id)}
                </div>
              </div>
            );
          })}
        </div>
      ) : showResults ? (
        <div className="no-pets-message">Данные отсутствуют</div>
      ) : null}

      {showCameraModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Выберите камеру для отслеживания</h3>
            <div className="cameras-list">
              {cameras.length > 0 ? (
                cameras.map(camera => (
                  <div 
                    key={camera.id} 
                    className={`camera-item ${selectedCameraId === camera.id ? 'selected' : ''}`}
                    onClick={() => {
                      const newCameraId = camera.id;
                      setSelectedCameraId(newCameraId);
                      window.appGlobals.selectedCameraId = newCameraId;
                    }}
                  >
                    {camera.room}
                  </div>
                ))
              ) : (
                <p className="no-cameras-message">Нет доступных камер</p>
              )}
            </div>
            <div className="modal-buttons">
              <button onClick={() => {
                setShowCameraModal(false);
                setPendingDuration(null);
                setSelectedCameraId(null);
                window.appGlobals.selectedCameraId = null;
              }}>Отмена</button>
              <button 
                onClick={confirmCameraSelection}
                disabled={!selectedCameraId}
              >
                Подтвердить
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EventsPage;
